import React,{useState,useEffect} from 'react';
import {Sidebar} from 'primereact/sidebar';
import {DataTable} from 'primereact/datatable';
import {Panel} from 'primereact/panel';
import {Column} from 'primereact/column';
import {Button} from 'primereact/button';
import EditPagos from './EditPagos.js'
import ServicesProductos from './ServicesProductos.js';
import Factura from './Factura.js';
import {ScrollPanel} from 'primereact/scrollpanel';
import {Toolbar} from 'primereact/toolbar';
import {InputSwitch} from 'primereact/inputswitch';

const SidebarV=({
  FullScreen,
  setFullScreen,
  Total,
  State,
  TipoVenta,
  Servicios,
  Productos,
  IdTicket,
  setIdTicket,
  getdata,
  getFormaPagos,
  FormaPagos,
  leftContents
}) => {

  return (
    <React.Fragment>
      {/* <Sidebar
        visible={FullScreen}
        fullScreen
        // baseZIndex={1000000}
        onHide={() => setFullScreen(0)}> */}

      <Toolbar left={leftContents} />
      <div className="p-d-flex">

        {/* <h1 style={{fontWeight: 'normal'}}>Panel de Pagos </h1> */}
        <Panel header="Ticket" className="p-mr-2" scrollable disable>
          <div className="p-grid p-fluid dashboard">
            <div className="p-col-12 p-lg-6">
              <div className="card summary">
                <span className="title">Total del Ticket</span>
                <span className="detail"></span>
                <span className="count revenue">${Total}</span>
              </div>
            </div>
            <div className="p-col-12 p-lg-6">
              <div className="card summary">
                <span className="title">Status</span>
                <span className="detail"></span>
                {State==='no pagado'? <span className="count purchases">No Pagado</span>:''}
                {State==='pagado'? <span className="count visitors">Pagado</span>:''}

              </div>
            </div>
            <div className="p-col-12 p-lg-12">
              <div className="card summary">
                <span className="title">Descueto adultos mayores o discapacidad</span>
                <span className="detail"></span>
                {State==='no pagado'? <span className="count purchases">No Pagado</span>:''}
                {State==='pagado'? <span className="count visitors">Pagado</span>:''}

              </div>
            </div>

            <Button
              label=" Nueva Venta De Productos"
              icon="pi pi-plus"
              className="p-button-success p-mr-2"
              onClick={() => {
                setFullScreen(0)
                generarticket();
                setFullScreen(1);
                setTipoVenta(false);
              }}
            />
          </div>
          <div>
            <ServicesProductos
              Productos={Productos}
              IdTicket={IdTicket}
              setIdTicket={setIdTicket}
              getdata={getdata}
              TipoVenta={TipoVenta}
              Servicio={Servicios}
              State={State} />
          </div>
        </Panel>
        <Panel header="Ticket" className="p-mr-2" scrollable>
          <Factura IdTicket={IdTicket} Total={Total} getFormaPagos={getFormaPagos}
            FormaPagos={FormaPagos} getdata={getdata} />
          <EditPagos IdTicket={IdTicket} Total={Total} State={State} getFormaPagos={getFormaPagos}
            FormaPagos={FormaPagos} getdata={getdata} />
        </Panel>

      </div>
      <Button type="button" onClick={() => setFullScreen(0)} label="Cancel" className="p-button-secondary" />
      <Button type="button" onClick={() => setFullScreen(0)} label="Guardar" className="p-button-success" />
      {/* </Sidebar> */}

    </React.Fragment>
  )

}

export default SidebarV;
