import React from 'react';
import { Dropdown } from 'primereact/dropdown';
import {DataTable} from 'primereact/datatable';
import {Column} from 'primereact/column';

const servicios = (props) => {
    console.log("[Servicios]props:");
    console.log(props);


    let serviciosPosibles = [...props.servicios];
    let serviciosSeleccionados = [...props.cita.arrayServicios];


    //Validacion para que no pueda repetir servicios al agregar
    if (serviciosSeleccionados.length > 0){
        console.log(props.servicios);
        console.log(props.cita.arrayServicios);

        serviciosSeleccionados.forEach(
            element => {
             const index = serviciosPosibles.findIndex(s => s.label == element.servicio)
                if (index != -1) {
                    serviciosPosibles.splice(index, 1);
                }
            }
        )
    }



   const tablaServiciosSelccionados =  <React.Fragment>

    <div classNames="content-section">
    <DataTable value={props.cita.arrayServicios}  globalFilter={props.globalFilter} paginatorPosition="both" selectionMode="single" header="Servicios Seleccionados" paginator={false} rows={10}
  responsive={true}>
          <Column field="servicio" header="Servicio" sortable={false} />
        <Column body={props.actionSubirCantidadServicioTemplate} header="Cantidad" sortable={true}/>
        <Column body={props.serviciosActionBodyTemplate}></Column>
  </DataTable>
    </div>

   </React.Fragment>

    const noHayServiciosSelccionados = <React.Fragment>
        <p>No hay servicios seleccionados...</p>
    </React.Fragment>

    return(
        <React.Fragment>
        <div className="content-section implementation p-dropdown">
        <Dropdown
        key = "DropDownServicios"
        placeholder = "Selecciona un servicio"      
        value={props.cita.idServicioSeleccionado}
        options={serviciosPosibles} 
        onChange={(e) => props.onChangeServicio(e)}
        dataKey="value" 
        filter />
        </div>

        {props.cita.arrayServicios.length == 0 ? noHayServiciosSelccionados : tablaServiciosSelccionados}
        </React.Fragment>

    )
}

export default servicios;
