import React from 'react';
import { Dropdown } from 'primereact/dropdown';
import {InputNumber} from 'primereact/inputnumber';
import {Button} from 'primereact/button';
import {DataTable} from 'primereact/datatable';
import {Column} from 'primereact/column';

const productos = (props) => {



    let productosPosibles = [...props.productos];
    let productosSeleccionados = [...props.cita.arrayProductos];

    //Validacion para que no pueda repetir servicios al agregar
    if (productosSeleccionados.length > 0){
        console.log(props.productos);
        console.log(props.cita.arrayProductos);

        productosSeleccionados.forEach(
            element => {
             const index = productosPosibles.findIndex(s => s.label == element.producto)
                if (index != -1) {
                    productosPosibles.splice(index, 1);
                }
            }
        )
    }


   const tablaServiciosSelccionados =  <React.Fragment>

    <div classNames="content-section">
    <DataTable value={props.cita.arrayProductos}  globalFilter={props.globalFilter} paginatorPosition="both" selectionMode="single" header="Productos Seleccionados" paginator={false} rows={10}
  responsive={true}>
          <Column field="producto" header="Producto/Medicamento" sortable={false} />
        <Column field="cantidad" header="Cantidad" sortable={true}/>
        <Column body={props.serviciosActionBodyTemplate}></Column>
  </DataTable>
    </div>

   </React.Fragment>

    const noHayServiciosSelccionados = <React.Fragment>
        <p>No hay productos seleccionados...</p>
    </React.Fragment>

    return(
        <React.Fragment>
        <div className="p-d-flex" style={{height: '100px'}}>
        <div className="p-mr-2 p-as-center">
        <div className="content-section implementation p-dropdown">
        <Dropdown
        key = "DropDownProductos"
        placeholder = "Selecciona un productos"      
        value={props.cita.idProductoSeleccionado}
        options={productosPosibles} 
        onChange={(e) => props.onChangeProducto(e)} 
        filter />
        </div>
        </div>

        <div className="p-mr-2 p-as-center">
        <InputNumber value={props.cita.cantidadProductoSeleccionado}  min={1} max={15} mode="decimal" showButtons buttonLayout="vertical" style={{width: '2em'}} onChange = {(e) => props.onCantidadProductoChange(e)}
        decrementButtonClassName="p-button-secondary" incrementButtonClassName="p-button-secondary" incrementButtonIcon="pi pi-plus" decrementButtonIcon="pi pi-minus" />
        </div>



        <div className="p-mr-2 p-as-center">
        <Button label="Agregar +" className="p-button-raised p-button-rounded" onClick={() => props.agregarProducto()}/>
        </div>
        </div>

        {props.cita.arrayServicios.length == 0 ? noHayServiciosSelccionados : tablaServiciosSelccionados}
        </React.Fragment>

    )
}

export default productos;
