import React,{useState,useEffect} from "react";
import Contenedor,{Alert} from '../componentsPrincipales/Alert.jsx'
import {Toolbar} from 'primereact/toolbar';
import {Button} from 'primereact/button';
import {Dialog} from 'primereact/dialog';
import TablaS from '../componentsPrincipales/TablaL/TablaMerca'
import Fetch from '../componentsPrincipales/Fetch/FetchN.js'
import Formulario from './components/formulario'



const AdminBonos=() => {
  const [Data,setData]=useState([])
  const [OpenForm,setOpenForm]=useState(false)
  const [DeleteForm,setDeleteForm]=useState(false)
  const [StateData,setStateData]=useState(
    {
      idcat_bonos: false,
      descripcion: '',
      cantidad: ''
    }
  )

  const Columnas=[
    {field: "descripcion",header: "Descripci√≥n"},
    {field: "cantidad",header: "Cantidad"}
  ]

  useEffect(() => {
    funget();
  },[]);

  const clearData=() => {
    setStateData(
      {
        idcat_bonos: false,
        descripcion: '',
        cantidad: ''
      }
    )
  }

  const validSaveUpdate=() => {
    if(StateData.idcat_bonos) {
      return true;
    } else {
      return false;
    }
  }

  const funget=() => {
    Fetch('api/bonos').then(response => {
      setData(response.data)
    }).catch(err => {
      console.log(err);
      Alert('warn',err);
    });
  }

  const saveDataEdit=() => {
    if(validSaveUpdate()) {
      funUpdate();
      setOpenForm(false);
      clearData();
    } else {
      funInsert();
      setOpenForm(false);
      clearData();
    }
  }

  const funInsert=() => {

    if(
      StateData.descripcion!==''&&
      StateData.cantidad!==''
    ) {
      let formData=new FormData();
      formData.append("descripcion",StateData.descripcion);
      formData.append("cantidad",StateData.cantidad);
      Fetch(`api/bonos`,{
        method: "POST",
        body: formData
      }).then(response => {
        if(response.status) {
          funget()
          Alert('success','Nuevo Bono Registrado');
        } else {
          Alert('warn',response.err);
        }
      }).catch(err => {
        console.log(err);
        Alert('warn',err);
      });
    }
  }

  const funUpdate=() => {
    if(
      StateData.idcat_bonos&&
      StateData.descripcion!==''&&
      StateData.cantidad!==''
    ) {
      let formData=new FormData();
      formData.append("idcat_bonos",StateData.idcat_bonos)
      formData.append("descripcion",StateData.descripcion);
      formData.append("cantidad",StateData.cantidad);
      Fetch(`api/bonos`,{
        method: "PUT",
        body: formData
      }).then(response => {
        if(response.status) {
          funget()
          Alert('success','Informacion Actualizada');
        }
      }).catch(err => {
        console.log(err);
        Alert('warn',err);
      });
    }
  }

  const funDeleted=() => {
    Fetch(`api/bonos/${StateData.idcat_bonos}`,{
      method: "DELETE"
    }).then(response => {
      if(response.status) {
        funget()
        Alert('success','Registro Eliminado!!!');
      }
    }).catch(err => {
      console.log(err);
      Alert('warn',err);
    });
  }

  const DataEdit=rowData => {
    if(rowData.idcat_bonos)
      setStateData(
        {
          idcat_bonos: rowData.idcat_bonos,
          descripcion: rowData.descripcion,
          cantidad: rowData.cantidad
        }
      )
  }

  const DialogFooter=(
    <>
      <Button
        label="Cancelar"
        icon="pi pi-times"
        className="p-button-text"
        onClick={() => {
          clearData()
          setOpenForm(false)
        }
        }
      />
      <Button
        label="Guardar"
        icon="pi pi-check"
        className="p-button-text"
        onClick={() => saveDataEdit()}
      />
    </>
  );

  const deleteDialogFooter=(
    <>
      <Button
        label="No"
        icon="pi pi-times"
        className="p-button-text"
        onClick={() => {setDeleteForm(false)}}
      />
      <Button
        label="Yes"
        icon="pi pi-check"
        className="p-button-text"
        onClick={() => {
          funDeleted()
          funget()
          setDeleteForm(false)
        }}
      />
    </>
  );

  const leftContents=(
    <React.Fragment>
      <Button
        label="Nuevo"
        icon="pi pi-plus"
        className="p-button-success p-mr-2"
        onClick={() => {
          clearData()
          setOpenForm(true)
        }}
      />
    </React.Fragment>
  )

  return (
    <React.Fragment>
      <Contenedor />
      <div className="datatable-crud-demo">
        <div className="card">
          <Toolbar left={leftContents} />
        </div>
      </div>

      <TablaS
        Data={Data}
        Columnas={Columnas}
        OpenForm={OpenForm}
        setOpenForm={setOpenForm}
        DataEdit={DataEdit}
        DeleteForm={DeleteForm}
        setDeleteForm={setDeleteForm}
      />

      <Dialog
        visible={OpenForm}
        style={{width: '50%'}}
        header="Mercancia"
        modal
        className="p-fluid"
        footer={DialogFooter}
        onHide={() => {setOpenForm(false)}}>
        {/* <Panel header="Producto"> */}
        {/* Campo de clave */}
        {/* <div className="p-grid"> */}
        {OpenForm? (
          <Formulario
            StateData={StateData}
            setStateData={setStateData}
            OpenForm={OpenForm} setOpenForm={setOpenForm}
          />):''}
        {/* </div> */}
        {/* </Panel> */}
      </Dialog>

      <Dialog
        visible={DeleteForm}
        style={{width: '50%'}}
        header="Confirm"
        modal
        footer={deleteDialogFooter}
        onHide={() => {setDeleteForm(false)}}>
        <div className="confirmation-content">
          <i className="pi pi-exclamation-triangle p-mr-3" style={{fontSize: '2rem'}} />
          {DataEdit&&<span>Estas seguro de eliminar registro?</span>}
        </div>
      </Dialog>

    </React.Fragment>
  )

}


export default AdminBonos;
