import React,{useState,useEffect} from 'react';
import {Dropdown} from 'primereact/dropdown';
import {InputNumber} from 'primereact/inputnumber';
import {Button} from 'primereact/button';
import {DataTable} from 'primereact/datatable';
import {Column} from 'primereact/column';
import {InputSwitch} from 'primereact/inputswitch';
import Fetch from '../../componentsPrincipales/Fetch/FetchN.js'
import Contenedor,{Alert} from '../../componentsPrincipales/Alert.jsx'
import {SelectButton} from 'primereact/selectbutton';
import {InputText} from "primereact/inputtext";

const Factura=({IdTicket,setIdTicket,getdata}) => {

  const [CatProductos,setCatProductos]=useState([])
  const [SelectProduct,setSelectProduct]=useState()
  const [Cantidad,setCantidad]=useState()
  const [Status,setStatus]=useState()

  const options=['Si','No'];
  const [value1,setValue1]=useState('Si');
  const addProductos=() => {
    let datos=new FormData()
    datos.append('idTicket',IdTicket)
    datos.append('idMedicina',SelectProduct.idmedicamentos)
    datos.append('cantidad',Cantidad)

    console.log(`IdTicket: ${IdTicket} SelectProduct: ${SelectProduct.idmedicamentos}Cantidad: ${Cantidad}`)

    Fetch('api/tickets/add/medicina',{method: "PUT",body: datos})
      .then(response => {
        // console.log(response)
        getdata();
        Alert('success','Se agrego a la cuenta ');

      })
  }

  const deleteProductos=(rowData) => {
    let datos=new FormData();
    datos.append('idTicket',IdTicket)
    datos.append('idMedicina',rowData.id)

    Fetch('api/tickets/delete/medicina',{method: "delete",body: datos})
      .then(response => {
        console.log(response)
        getdata();
        Alert('success','Se elimino de la cuenta');

      });
  }

  useEffect(() => {
    Fetch('api/mercancia')
      .then(response => {
        setCatProductos(response.data);
      }).catch(err => {
        console.log(err);
      })
  },[]);



  const tablaProductosSelccionados=() => {
    return (
      <React.Fragment>
        <div classNames="content-section">
          {/* <DataTable value={props.cita.arrayProductos} globalFilter={props.globalFilter} paginatorPosition="both" selectionMode="single" header="Productos Seleccionados" paginator={false} rows={10}
            responsive={true}>
            <Column field="servicio" header="Servicio" sortable={false} />
            <Column field="cantidad" header="Cantidad" sortable={true} />
            <Column body={props.ProductosActionBodyTemplate}></Column>
          </DataTable> */}
        </div>
      </React.Fragment>)
  }


  const actionCitasEnEsperaBodyTemplate=(rowData) => {
    return (
      <><Button icon="pi pi-times" className="p-button-rounded p-button-danger p-mr-2" onClick={() => deleteProductos(rowData)} /></>
    );
  }

  return (
    <React.Fragment>
      <Contenedor />
      <h5>Factura</h5>

      <div className="p-mr-2 p-as-center">

        <InputSwitch checked={Status===0? false:true} onChange={(e) => setStatus(e.value? 1:0)} />
        <div className="p-col-12 p-lg-12">
          <div className="card summary">
            <span className="title">Descueto adultos mayores o discapacidad</span>
            <span className="detail"></span>
            <SelectButton value={value1} options={options} onChange={(e) => setValue1(e.value)} />

          </div>

          <div className="card summary">
            <span className="title">Descuetos extras</span>
            <span className="detail">
              <div className="p-inputgroup">
                <span className="p-inputgroup-addon">$</span>
                <InputText keyfilter="pnum" />
              </div></span>


          </div>

        </div>
      </div>

    </React.Fragment>
  )
}

export default Factura;
