import React,{useState,useEffect} from 'react';
import Fetch from '../../../componentsPrincipales/Fetch/FetchN.js'
import Contenedor,{Alert} from '../../../componentsPrincipales/Alert.jsx'
import {Button} from 'primereact/button';
import {Dialog} from 'primereact/dialog';
import Formulario from '../../../moduloClientes/components/formulario.js'

const NuevoCliente=(props) => {

  const [OpenForm,setOpenForm]=useState(true)
  const [Idcat_clientes,setIdcat_clientes]=useState(false)
  const [Nombre,setNombre]=useState()
  const [ApellidoP,setApellidoP]=useState()
  const [ApellidoM,setApellidoM]=useState()
  const [Telefono,setTelefono]=useState()
  const [Correo,setCorreo]=useState()
  const [Direccion,setDireccion]=useState()
  const [NotasMedicas,setNotasMedicas]=useState()
  const [NotasEspe,setNotasEspe]=useState()
  const [Alergias,setAlergias]=useState()

  const clearData=() => {
    setIdcat_clientes(false)
    setNombre()
    setApellidoP()
    setApellidoM()
    setTelefono()
    setCorreo()
    setDireccion()
    setNotasMedicas()
    setNotasEspe()
    setAlergias()
  }

  const saveDataEdit=() => {
    funInsert();
    setOpenForm(false);
    clearData();
  }



  const funInsert=() => {

    let formData=new FormData();
    // formData.append("id",id);
    if(Nombre!==undefined) formData.append("nombre",Nombre);
    if(ApellidoP!==undefined) formData.append("apellidoP",ApellidoP);
    if(ApellidoM!==undefined) formData.append("apellidoM",ApellidoM);
    if(Telefono!==undefined) formData.append("telefono",Telefono.replace(/[()]/gi,'').replace(/-/gi,'').replace(/[ ]/gi,''));
    if(Correo!==undefined) formData.append("correo",Correo);
    if(Direccion!==undefined) formData.append("direccion",Direccion);
    if(Alergias!==undefined) formData.append("alergias",Alergias);
    // formData.append("notas_medicas",NotasMedicas);
    // formData.append("notas_especiales",NotasEspe);

    Fetch(`api/clientes`,{
      method: "POST",
      body: formData
    }).then(response => {
      if(response.status) {
        Alert('success','Nuevo Usuario Registrado!!!');
        let nombreCompleto=Nombre+' '+ApellidoP+' '+ApellidoM;
        clearData()
        props.despuesDeAgregar(response.data,nombreCompleto);
      } else {
        Alert('warn',response.err);
      }
    }).catch(err => {
      console.log(err);
      Alert('warn',err);
    });
  };


  const DialogFooter=(
    <>
      <Button
        label="Cancelar"
        icon="pi pi-times"
        className="p-button-text"
        onClick={() => {
          clearData()
          setOpenForm(false)
          props.closeNewClienteDialog()
        }
        }
      />
      <Button
        label="Guardar"
        icon="pi pi-check"
        className="p-button-text"
        onClick={() => saveDataEdit()}
      />
    </>
  );



  return <Dialog
    visible={OpenForm}
    style={{width: '50%'}}
    header="Clientes"
    modal
    className="p-fluid"
    footer={DialogFooter}
    onHide={() => {
      setOpenForm(false)
      props.closeNewClienteDialog()
    }}>
    {/* <Panel header="Producto"> */}
    {/* Campo de clave */}
    {/* <div className="p-grid"> */}
    {OpenForm? (<Formulario
      Idcat_clientes={Idcat_clientes} setIdcat_clientes={setIdcat_clientes}
      Nombre={Nombre} setNombre={setNombre}
      ApellidoP={ApellidoP} setApellidoP={setApellidoP}
      ApellidoM={ApellidoM} setApellidoM={setApellidoM}
      Telefono={Telefono} setTelefono={setTelefono}
      Correo={Correo} setCorreo={setCorreo}
      Direccion={Direccion} setDireccion={setDireccion}
      NotasMedicas={NotasMedicas} setNotasMedicas={setNotasMedicas}
      NotasEspe={NotasEspe} setNotasEspe={setNotasEspe}
      Alergias={Alergias} setAlergias={setAlergias}
      OpenForm={OpenForm} setOpenForm={setOpenForm} />):''}
    {/* </div> */}
    {/* </Panel> */}
  </Dialog>
}

export default NuevoCliente;
