import React,{useState,useEffect} from 'react';
import {Panel} from 'primereact/panel';
import {Button} from 'primereact/button';
import EditPagos from './EditPagos.js'
import ServicesProductos from './ServicesProductos.js';
import Factura from './Factura.js';

const SidebarV=({
  // FullScreen,
  setFullScreen,
  Total,
  State,
  TipoVenta,
  Servicios,
  Productos,
  IdTicket,
  setIdTicket,
  getdata,
  getFormaPagos,
  FormaPagos,
  promociones
}) => {

  const [checked1,setChecked1]=useState(false);

  return (
    <React.Fragment>
      <div className="p-grid p-shadow-24">
        <div className="p-col-9">
          <Panel header="Ticket" className=" p-p-3" scrollable>
            <div className="p-grid p-fluid dashboard">
              <div className="p-col-12 p-lg-6">
                <div className="card summary">
                  <span className="title">Total del Ticket</span>
                  <span className="detail"></span>
                  <span className="count revenue">${Total}</span>
                </div>
              </div>
              <div className="p-col-12 p-lg-6">
                <div className="card summary">
                  <span className="title">Status</span>
                  <span className="detail"></span>
                  {State==='no pagado'? <span className="count purchases">No Pagado</span>:''}
                  {State==='pagado'? <span className="count visitors">Pagado</span>:''}
                </div>
              </div>
            </div>

            <ServicesProductos
              Productos={Productos}
              IdTicket={IdTicket}
              setIdTicket={setIdTicket}
              getdata={getdata}
              TipoVenta={TipoVenta}
              Servicio={Servicios}
              State={State} />

          </Panel>
        </div>
        <div className="p-col-3">
          <Panel header="Detalles del pago " className=" p-p-3" scrollable>
            <Factura IdTicket={IdTicket} Total={Total} getFormaPagos={getFormaPagos}
              FormaPagos={FormaPagos} getdata={getdata} promociones={promociones}  />
            <EditPagos IdTicket={IdTicket} Total={Total} State={State} getFormaPagos={getFormaPagos}
              FormaPagos={FormaPagos} getdata={getdata} />
            <div className="p-d-flex p-jc-center">
              <div ><Button type="button" onClick={() => setFullScreen(0)} label="Cancel" className="p-button-secondary p-mr-2 p-mb-2" /></div>
              <div><Button type="button" onClick={() => setFullScreen(0)} label="Guardar" className="p-button-success p-mb-2" /></div>
            </div>
          </Panel>
        </div>
      </div>
    </React.Fragment>
  )

}

export default SidebarV;
