import React,{useState,useEffect} from 'react';
import {Dropdown} from 'primereact/dropdown';
import {InputNumber} from 'primereact/inputnumber';
import {Button} from 'primereact/button';
import {DataTable} from 'primereact/datatable';
import {Column} from 'primereact/column';
import Fetch from '../../componentsPrincipales/Fetch/FetchN.js'
import Contenedor,{Alert} from '../../componentsPrincipales/Alert.jsx'
import {Panel} from 'primereact/panel';
import {Fieldset} from 'primereact/fieldset';


const ServicesProductos=({Productos,IdTicket,setIdTicket,getdata,TipoVenta,Servicio,State}) => {

  const [CatProductos,setCatProductos]=useState([])
  const [CatPromoProduct,setCatPromoProduct]=useState();
  const [CatSinPromoProduct,setCatSinPromoProduct]=useState();
  const [SelectProduct,setSelectProduct]=useState()
  const [Cantidad,setCantidad]=useState()

  const addProductos=() => {
    if(IdTicket!==0&&SelectProduct!==undefined&&Cantidad!==undefined&&Cantidad>0) {
      let datos=new FormData()
      datos.append('idTicket',IdTicket)
      datos.append('idMedicina',SelectProduct.idmedicamentos)
      datos.append('cantidad',Cantidad)
      Fetch('api/tickets/add/medicina',{method: "PUT",body: datos})
        .then(response => {
          getdata();
          Alert('success','Se agrego a la cuenta ');
        })
    } else {
      Alert('warn','Debes seleccionar un producto');
    }

  }

  const deleteProductos=(rowData) => {
    // deletePromoProductos(rowData)
    let datos=new FormData();
    datos.append('idTicket',IdTicket)
    datos.append('idMedicina',rowData.id)

    Fetch('api/tickets/delete/medicina',{method: "delete",body: datos})
      .then(response => {
        console.log(response)
        getdata();
        Alert('success','Se elimino de la cuenta');
      });
  }

  const updateData=() => {
    Fetch('api/mercancia/venta')
      .then(response => {
        setCatProductos(response.data);
      }).catch(err => {
        console.log(err);
      })

    Fetch(`api/tickets/sin-promo-producto/${IdTicket}`)
      .then(response => {
        setCatSinPromoProduct(response.data);
      }).catch(err => {
        console.log(err);
      })

    Fetch(`api/tickets/promo-producto/${IdTicket}`)
      .then(response => {
        setCatPromoProduct(response.data);
      }).catch(err => {
        console.log(err);
      })

  }
  useEffect(() => {
    updateData()
  },[]);


  const actionCitasEnEsperaBodyTemplate=(rowData) => {
    return (
      <>
        {/* <Button icon="pi pi-pencil" className="p-button-rounded p-button-warning p-mr-2" onClick={() => this.editCita(rowData)} /> */}
        <Button disabled={State==='pagado'} icon="pi pi-times" className="p-button-rounded p-button-danger p-mr-2" onClick={() => deleteProductos(rowData)} />
        {/* <Button icon="pi pi-upload" className="p-button-rounded p-button-success p-mr-2" onClick={() => this.showValidacionPINDialogAtender(rowData)} /> */}
      </>
    );
  }



  return (
    <React.Fragment>
      <Contenedor />
      <Panel header="Resumen Conceptos" className=" p-mb-2">
        <div className="p-grid p-shadow-24">

          <div className="p-col-6">
            <Fieldset legend="Productos" toggleable>
              <div className="p-mr-2 p-as-center">
                {State==='no pagado'? <React.Fragment>
                  <div className="content-section implementation p-dropdown">
                    <Dropdown
                      value={SelectProduct}
                      options={CatProductos}
                      onChange={(e) => {
                        setSelectProduct(e.target.value)
                      }}
                      optionLabel="nombre"
                      placeholder="Seleccionar Metodo de Pago"
                      filter
                    />
                  </div>
                  <InputNumber id="minmax-buttons"
                    value={Cantidad}
                    min={1}
                    max={15}
                    mode="decimal"
                    showButtons
                    onChange={(e) => {setCantidad(e.value)}}
                    incrementButtonIcon="pi pi-plus"
                    decrementButtonIcon="pi pi-minus"
                  />

                  <Button label="Agregar Producto " className="p-button-raised p-button-rounded" onClick={() => addProductos()} />
                  <hr></hr>
                </React.Fragment>:''}

                <DataTable
                  value={Productos}
                  scrollable>
                  <Column field="nombre" header="Concepto" ></Column>
                  <Column field="cantidad" header="Cantidad"></Column>
                  <Column field="costo" header="$"></Column>
                  <Column body={actionCitasEnEsperaBodyTemplate} ></Column>
                </DataTable>
              </div>
            </Fieldset>
          </div>


          <div className="p-col-6">
            {TipoVenta? (
              <Fieldset legend="Servicios" toggleable>
                <DataTable value={Servicio} scrollable>
                  <Column field="nombre" header="Concepto"></Column>
                  <Column field="cantidad" header="Cantidad"></Column>
                  <Column field="costo" header="Costo"></Column>
                </DataTable>
              </Fieldset>
            ):''}
          </div>
        </div>
      </Panel>
    </React.Fragment>
  )
}

export default ServicesProductos;
