import React from 'react';
import classNames from 'classnames';
import {InputText} from 'primereact/inputtext';
import {Dropdown} from 'primereact/dropdown';
import {Password} from 'primereact/password';
import {ColorPicker} from 'primereact/colorpicker';
import {InputSwitch} from 'primereact/inputswitch';
import {InputNumber} from 'primereact/inputnumber'


const Formulario=({StateData,setStateData,OpenForm}) => {

  return (
    <React.Fragment>
      <div className="p-field">
        <p>
          <label htmlFor="clave">Nombre</label>
        </p>
        <InputText
          value={StateData.nombre}
          onChange={(e) => setStateData({...StateData,nombre: e.target.value})}
          required
          autoFocus
          className={classNames({'p-invalid': OpenForm&&!StateData.nombre})} />
        {OpenForm&&!StateData.nombre&&<small className="p-invalid">Campo es requerido.</small>}
      </div>

      <div className="p-field">
        <p>
          <label htmlFor="clave">Descripcion</label>
        </p>
        <InputText
          value={StateData.descripcion}
          onChange={(e) => setStateData({...StateData,descripcion: e.target.value})}
          required
          autoFocus
          className={classNames({'p-invalid': OpenForm&&!StateData.descripcion})} />
        {OpenForm&&!StateData.descripcion&&<small className="p-invalid">Campo es requerido.</small>}
      </div>
      {/* Campo de Descripcion */}
      <div className="p-field">
        <p>
          <label htmlFor="descripcion">Precio</label>
        </p>
        <InputNumber
          value={StateData.precio}
          onChange={(e) => setStateData({...StateData,precio: e.value})}
          required
          autoFocus
          mode="currency"
          currency="MXN"
          locale="es-MX"
          className={classNames({'p-invalid': OpenForm&&!StateData.precio})} />
        {OpenForm&&!StateData.precio&&<small className="p-invalid">Campo es requerido.</small>}
      </div>
      <div className="p-field">
        <p>
          <label htmlFor="descripcion">Precio con descuento</label>
        </p>
        <InputNumber
          value={StateData.precio_descuento}
          onChange={(e) => setStateData({...StateData,precio_descuento: e.value})}
          required
          autoFocus
          mode="currency"
          currency="MXN"
          locale="es-MX" />
      </div>
      <div classname="row col-12">
        *Si el precio de descuento es 0 significa que no se aplicará descuento alguno y se cobrará a precio normal*
          </div>
    </React.Fragment>
  )

}



export default Formulario;
