import React from 'react';
import classNames from 'classnames';
import {InputText} from 'primereact/inputtext';
import {Dropdown} from 'primereact/dropdown';
import {Password} from 'primereact/password';
import {ColorPicker} from 'primereact/colorpicker';
import {InputSwitch} from 'primereact/inputswitch';



const Formulario=({
  Idcat_usuario,
  Nombre,setNombre,
  ApellidoP,setApellidoP,
  ApellidoM,setApellidoM,
  TipoNomina,setTipoNomina,
  Correo,setCorreo,
  Colors,setColors,
  Pass,setPass,
  PassC,setPassC,
  OpenForm,
  CatTipoNomina,
  Status,setStatus}) => {


  return (
    <React.Fragment>
      {/* Campo de nombre */}
      <div className="p-field">
        <label >Nombre</label>
        <InputText
          value={Nombre}
          onChange={(e) => setNombre(e.target.value)}
          required
          autoFocus
          className={classNames({'p-invalid': OpenForm&&!Nombre})} />
        {OpenForm&&!Nombre&&<small className="p-invalid">Campo requerido</small>}
      </div>
      {/* Campo de Apellido Paterno */}
      <div className="p-field">
        <label>Apellido Paterno</label>
        <InputText
          value={ApellidoP}
          onChange={(e) => setApellidoP(e.target.value)}
          required
          autoFocus
          className={classNames({'p-invalid': OpenForm&&!ApellidoP})} />
        {OpenForm&&!ApellidoP&&<small className="p-invalid">Campo requerido</small>}
      </div>
      {/* Apellido Materno */}
      <div className="p-field ">
        <label>Apellido Materno</label>
        <InputText
          value={ApellidoM}
          onChange={(e) => setApellidoM(e.target.value)}
          required
          className={classNames({'p-invalid': OpenForm&&!ApellidoM})} />
        {OpenForm&&!ApellidoM&&<small className="p-invalid">Campo requerido</small>}
      </div>
      {/* Campo Tipo de Nomina */}
      <div className="p-field ">
        <label>Tipo de Nomina</label>
        <select className="form-control" defaultValue={TipoNomina} onChange={(e) => setTipoNomina(e.target.value)}>
          <option value="">Selecciona un tipo de nomina</option>
          {CatTipoNomina.map((nomina,key) => {
            return (<option key={key} value={`${nomina.idcat_tipo_nomina}`}>{nomina.descripcion}</option>)
          })}
        </select>
      </div>
      {/* Color */}
      <div className="p-field">
        <label>Selcciona un Color</label>
        <ColorPicker value={Colors} onChange={(e) => setColors(e.value)}></ColorPicker>
      </div>
      {/* correo */}
      <div className="p-field">
        <label>Correo</label>
        <InputText
          value={Correo}
          onChange={(e) => setCorreo(e.target.value)}
          autoFocus
          className={classNames({'p-invalid': OpenForm&&!Correo})} />
        {OpenForm&&!Correo&&<small className="p-invalid">Campo requerido</small>}
      </div>
      {/**Contraseña */}

      <div className="p-field">
        <label>{!Idcat_usuario? ('Contraseña'):'Nueva contraseña'}</label>
        <Password onChange={(e) => setPass(e.target.value)} required />
      </div>

      {!Idcat_usuario? (
        <div className="p-field">
          <label>Confirma Contraseña</label>
          <Password value={PassC} onChange={(e) => setPassC(e.target.value)} required />
        </div>
      ):''}

      <h5>Estado de Empleado</h5>
      <InputSwitch checked={Status===0? false:true} onChange={(e) => setStatus(e.value? 1:0)} />

    </React.Fragment>
  )

}

export default Formulario;
