import React,{useState,useEffect} from 'react';
import {DataTable} from 'primereact/datatable';
import {Column} from 'primereact/column';
import {InputText} from 'primereact/inputtext';
import {Tag} from 'primereact/tag';

const TablaL=({
  Data,
  Columnas,
  setIdTicket,
  setTotal,
  setServicios,
  setProductos,
  setTipoVenta,
  getdata
}) => {

  const [GlobalFilter,setGlobalFilter]=useState()
  const [selectedProduct2,setSelectedProduct2]=useState(null);

  const onRowSelect=async (event) => {
    await setIdTicket(parseInt(event.data.idtickets,10))
    await setTotal()
    await setServicios()
    await setProductos()
    await getdata(parseInt(event.data.idtickets,10))
    await setTipoVenta(true)
  }

  const onRowUnselect=(event) => {
    alert('DesSelecciono')
  }


  const header=(
    <div className="table-header">
      <InputText
        type="search"
        onInput={(e) => setGlobalFilter(e.target.value)}
        placeholder="Buscar..." />
    </div>
  );

  return (
    <div className="datatable-crud-demo">
      <div className="card">
        <DataTable
          paginator
          rows={5}
          rowsPerPageOptions={[2,5,10,25]}
          globalFilter={GlobalFilter}
          header={header}
          value={Data}
          selection={selectedProduct2}
          onSelectionChange={e => setSelectedProduct2(e.value)}
          selectionMode="single"
          dataKey="id"
          onRowSelect={onRowSelect}
          onRowUnselect={onRowUnselect}>
          {Columnas.map((option,key) => {
            if(option.field==='Estatus') {
              return (
                <Column key={key} field={option.field} header={option.header} body={(rowData) => {
                  return (
                    <React.Fragment>
                      {rowData.Estatus==='Pagado'? <Tag className="p-mr-2" severity="success" value={rowData.Estatus}></Tag>:''}
                      {rowData.Estatus==='Sin Pagar'? <Tag className="p-mr-2" severity="danger" value={rowData.Estatus}></Tag>:''}

                    </React.Fragment>
                  );
                }} sortable></Column>
              )
            } else {
              return (<Column key={key} field={option.field} header={option.header} sortable></Column>)
            }
          }
          )}
        </DataTable>
      </div>
    </div>
  );
}

export default TablaL;
