import React, {Component} from 'react';
import {DataTable} from 'primereact/datatable';
import {Column} from 'primereact/column';
import {Button} from 'primereact/button';
import { Dropdown } from 'primereact/dropdown';
import { Calendar } from 'primereact/calendar';
import {InputText} from 'primereact/inputtext';
import Contenedor,{Alert} from '../componentsPrincipales/Alert.jsx'
import Fetch from '../componentsPrincipales/Fetch/FetchN.js'
import axios from 'axios';
import moment from 'moment';

export default class AppReportes extends Component {

    componentDidMount(){


    }

    constructor(props){
        super(props);
        this.state = {
            fechaFinal : moment().day() >= 3 ? moment().startOf('week').add('days', 3).format('DD/MM/YYYY') : moment().format('DD/MM/YYYY'),
            fechaInicial : moment().startOf('week').add('days', 3).subtract(6, "days").format('DD/MM/YYYY'),
            tipoPagoSeleccionado : 0,
            tipoPagoSeleccionadoLabel : "Todos los Usuarios",
            usuarioSeleccionado : 0,
            usuarioSeleccionadoLabel : "Mixto",
            listaUsuarios : [{label :"Todos los Usuarios", value : 0}, {label : "Todos los Podologos", value : 1}, {label : "Manuel Ortíz", value: 2}],
            listaTipoPago : [{label :"Mixto", value : 0}, {label : "Efectivo", value : 1}, {label : "Tarjeta", value: 2}],
            banderaSeleccion : false, //Bandera para saber sí mostrar la tabla o los parametros de seleccion
            globalFilter: null,
            arrayDiasTablaOrdenado  : null,
            headerTabla : [
              {label: "IdConsulta" , value: "idcita_consulta"},
              {label: "Fecha" , value: "fecha"},
              {label: "Preferencia" , value: "preferencia"},
              {label: "Tipo de Cita" , value: "tipoCita"},
              {label: "Estatus" , value: "estatusCita"},
              {label: "Podologo" , value: "podologo"},
              {label: "Cliente" , value: "cliente"},
              {label: "Servicios" , value: "servicios"},
              {label: "Medicamentos" , value: "medicamentos"},
            ],
            datosTabla : null
        }

        this.onChangeInitialDate = this.onChangeInitialDate.bind(this);
        this.onChangeEndDate = this.onChangeEndDate.bind(this);
        this.onUserChange = this.onUserChange.bind(this);
        this.obtenerReporte = this.obtenerReporte.bind(this);
        this.exportCSV=this.exportCSV.bind(this);



    }

    onChangeInitialDate(e){
        console.log('Entro a cambiar fecha Inicial');
        let fechaInicial={...this.state.fechaInicial};
        // const dateTimeFormat = new Intl.DateTimeFormat('en-GB', { year: 'numeric', month: '2-digit', day: '2-digit', hour: '2-digit', minute: '2-digit', hour12 : false })
        // fechaInicial = dateTimeFormat.format(e.value).toString().replace(',', '').concat(':00');
        fechaInicial = moment(e.value).format('DD/MM/YYYY');

        let fechaFinal = {...this.state.fechaFinal}

        fechaFinal = moment(e.value).add('days', 8).format('DD/MM/YYYY')//Le sumamos siempre 8 si cambian la fecha inicial

        this.setState({fechaInicial, fechaFinal});
      }

      onChangeEndDate(e){
        console.log('Entro a cambiar fecha Final');
        let fechaFinal={...this.state.fechaFinal};
        // const dateTimeFormat = new Intl.DateTimeFormat('en-GB', { year: 'numeric', month: '2-digit', day: '2-digit', hour: '2-digit', minute: '2-digit', hour12 : false })
        // fechaFinal = dateTimeFormat.format(e.value).toString().replace(',', '').concat(':00');
        fechaFinal = moment(e.value).format('DD/MM/YYYY');
        this.setState({fechaFinal});
      }

      onUserChange(e){
        let usuarioSeleccionado={...this.state.usuarioSeleccionado};
        let usuarioSeleccionadoLabel = {...this.state.usuarioSeleccionadoLabel};
        usuarioSeleccionado = e.value;
        usuarioSeleccionadoLabel = e.label;
        this.setState({usuarioSeleccionado});
      }


      exportCSV() {
        this.dt.exportCSV();
      }

      obtenerReporte(){
        //Consumir Servicios y rellenar tabla
        console.log(this.state.fechaInicial);
        console.log(this.state.fechaFinal);
        let fechaFin = moment(new Date(this.state.fechaFinal)).format('YYYY-MM-DD')

        console.log({fechaFin})


        var request = {
          fecha_inicio : moment(this.state.fechaInicial, 'DD/MM/YYYY').format('YYYY-MM-DD'),
          fecha_fin : moment(this.state.fechaFinal, 'DD/MM/YYYY').format('YYYY-MM-DD')
        }
        console.log('Request:')
        console.log(request)

        Fetch('api/citas/getFromDate', {
          method : 'POST',
          body : request
        }).then(values => {
            if(values.status){
              console.log(values.data)

              //Obtenemos los valores de la tabla
             let datosTabla = values.data.map(post => {   

              let valorTabla = {
                idcita_consulta : post.idcita_consulta,
                fecha : post.fecha,
                preferencia : post.preferencia ? 'Sí' : 'No' ,
                tipoCita : post.tipoCita,
                estatusCita : post.estatusCita,
                podologo : post.podologo.nombre,
                cliente : post.cliente.nombre,
                servicios : post.servicios.length == 0 ? "N/A": (post.servicios.map(serv => 
                  ( 
                     serv.nombre +" : " + serv.cantidad + " "
                  )
                  ).toString()),
                medicamentos : post.medicamentos.length == 0 ? "N/A": (post.medicamentos.map(med => 
                    ( 
                       med.nombre +" : " + med.cantidad + " "
                    )
                    ).toString())
                }
                return valorTabla;
             })

             console.log('DatosTabla:')
             console.log(datosTabla)

             this.setState({datosTabla});//Se actualiza el estado


            }else{
              Alert('Ocurrió un error al obtener el reporte');
            }
        })
      }





    render(){

        const header=(
            <div className="table-header">
              <h5 className="p-m-0">Reportes </h5>
              <h6 className="p-m-1">{this.state.fechaInicial + '-' + this.state.fechaFinal}</h6>
              <span className="p-input-icon-left">
                <i className="pi pi-search" />
                <InputText type="search" onInput={(e) => this.setState({globalFilter: e.target.value})} placeholder="Buscar..." />
              </span>
              <Button label="Exportar" icon="pi pi-upload" className="p-button-help" onClick={this.exportCSV} disabled = {this.state.datosTabla ?  false  : true} />
            </div>
          );

          //Agregamos dinamicamente los headers y llaves para valores
          const headersTabla = this.state.headerTabla.map( htabla => {
            let columna;
            if(htabla.value == 'fecha' ){
            columna = <Column field={htabla.value} header={htabla.label} sortable={true} style={{'font-weight' : 'bold'}} />
            }else {
             columna = <Column field={htabla.value} header={htabla.label} sortable={true} />
            }
            return columna;
          })

        return(
            <div>
                <div className="card card-w-title">
                    <h1>Reportes</h1>
                    <div className="p-grid">
                        <div className="p-col-12 p-md-6">
                            <p><b>Fecha Inicial:</b></p>
                            <div className="p-calendar-w-btn">
                                <Calendar showIcon={true}
                                dateFormat = 'dd/mm/yy'
                                value={
                                new Date(moment(this.state.fechaInicial, 'DD/MM/YYYY'))
                                } 
                                onChange={(e) => this.onChangeInitialDate(e)}>
                              </Calendar>
                            </div>
                        </div>

                        <div className="p-col-12 p-md-6">
                            <p><b>Fecha Final:</b></p>
                            <div className="p-calendar-w-btn">
                                <Calendar showIcon={true}
                                dateFormat = 'dd/mm/yy'
                                value={
                                new Date(moment(this.state.fechaFinal, 'DD/MM/YYYY'))
                                } 
                                onChange={(e) => this.onChangeEndDate(e)}>
                            </Calendar>
                            </div>
