import React,{useState,useEffect} from "react";
import {Button} from 'primereact/button';
import {Toolbar} from 'primereact/toolbar';
import Fetch from '../componentsPrincipales/Fetch/FetchN.js'
import {InputText} from "primereact/inputtext";
import Contenedor,{Alert} from '../componentsPrincipales/Alert.jsx'
import SiderBarV from './components/SiderBar.js'
import TablaVentas from './components/Ventas'
import {TabView,TabPanel} from 'primereact/tabview';


const Pagos=() => {
  const [FullScreen,setFullScreen]=useState(0)
  const [TipoVenta,setTipoVenta]=useState(true)
  const [IdTicket,setIdTicket]=useState(0);
  const [Total,setTotal]=useState()
  const [State,setState]=useState()
  const [FormaPagos,setFormaPagos]=useState([])
  const [Servicios,setServicios]=useState([])
  const [Productos,setProductos]=useState([])

  const getdata=(idTT=false) => {

    if(idTT) {
      Fetch(`api/tickets/get/${idTT}`)
        .then(response => {
          if(response.data) {
            setServicios(response.data.servicios)
            setProductos(response.data.medicamentos)
            setTotal(response.data.total)
            setState(response.data.estatus)
            getFormaPagos(idTT)
            setFullScreen(1);
          } else {
            Alert('error','No Existe este numero de ticket')
          }
        }).catch(error => {
          console.log(error)
        })
    } else {
      Fetch(`api/tickets/get/${IdTicket}`)
        .then(response => {
          if(response.data) {
            setServicios(response.data.servicios)
            setProductos(response.data.medicamentos)
            setTotal(response.data.total)
            setState(response.data.estatus)
            getFormaPagos(IdTicket)
            setFullScreen(1);
          } else {
            Alert('error','No Existe este numero de ticket')
          }
        }).catch(error => {
          console.log(error)
        })
    }
  }

  const getFormaPagos=(idTT=false) => {
    if(idTT) {
      Fetch(`api/tickets/get/formas-pago/${idTT}`)
        .then(response => {
          console.log(response)
          setFormaPagos(response.response)
        })
    } else {
      Fetch(`api/tickets/get/formas-pago/${IdTicket}`)
        .then(response => {
          console.log(response)
          setFormaPagos(response.response)
        })
    }
  }

  const generarticket=() => {
    let formData=new FormData();
    formData.append("idCita",0)
    Fetch(`api/tickets/create`,{
      method: "POST",
      body: formData
    }).then(response => {
      setIdTicket(response.data)
      getdata(response.data);
    }).catch(err => {
      console.log(err);
      // Alert('warn',err);
    });
  }

  const leftContents=() => (
    <React.Fragment>
      <Button
        label=" Nueva Venta De Productos"
        icon="pi pi-plus"
        className="p-button-success p-mr-2"
        onClick={() => {
          setFullScreen(0)
          generarticket();
          setFullScreen(1);
          setTipoVenta(false);
        }}
      />
    </React.Fragment>
  )

  useEffect(() => {
    if(FullScreen===0) {
      setFullScreen(0)
      setTipoVenta(true)
      setIdTicket(0);
      setTotal()
      setState()
      setFormaPagos([])
      setServicios([])
      setProductos([])

    }

  },[FullScreen])

  return (
    <React.Fragment>
      <Contenedor />
      <TabView activeIndex={FullScreen} onTabChange={(e) => setFullScreen(e.index)}>
        <TabPanel header="Tickets">
          <div className="card">
            <Toolbar left={leftContents} />
            <TablaVentas
              FullScreen={FullScreen}
              setFullScreen={setFullScreen}
              setIdTicket={setIdTicket}
              setTotal={setTotal}
              setServicios={setServicios}
              setProductos={setProductos}
              setTipoVenta={setTipoVenta}
              generarticket={generarticket}
              IdTicket={IdTicket}
              getdata={getdata}
              Servicios={Servicios}
              Productos={Productos}
            />

          </div>
        </TabPanel>
        <TabPanel header="Detalles" disabled>
          <SiderBarV
            FullScreen={FullScreen}
            setFullScreen={setFullScreen}
            Total={Total}
            State={State}
            TipoVenta={TipoVenta}
            Servicios={Servicios}
            Productos={Productos}
            IdTicket={IdTicket}
            setIdTicket={setIdTicket}
            getdata={getdata}
            getFormaPagos={getFormaPagos}
            FormaPagos={FormaPagos}
            leftContents={leftContents}
          />
        </TabPanel>
      </TabView>




    </React.Fragment>
  );
}
export default Pagos;
