import React,{useState,useEffect,Component} from 'react';
import classNames from 'classnames';
import {InputText} from 'primereact/inputtext';
import {InputTextarea} from 'primereact/inputtextarea';
import {InputMask} from 'primereact/inputmask';


const Formulario=({
  Idcat_clientes,setIdcat_clientes,
  Nombre,setNombre,
  ApellidoP,setApellidoP,
  ApellidoM,setApellidoM,
  Telefono,setTelefono,
  Correo,setCorreo,
  Direccion,setDireccion,
  NotasMedicas,setNotasMedicas,
  NotasEspe,setNotasEspe,
  Alergias,setAlergias,
  OpenForm,setOpenForm}) => {


  return (
    <React.Fragment>
      {/* Campo de nombre */}
      <div className="p-field">
        <label > <h4>Nombre:</h4></label>
        <InputText
          value={Nombre}
          onChange={(e) => setNombre(e.target.value)}
          required
          autoFocus
          className={classNames({'p-invalid': OpenForm&&!Nombre})} />
        {OpenForm&&!Nombre&&<small className="p-invalid">Campo requerido</small>}
      </div>
      {/* Campo de Apellido Paterno */}
      <div className="p-field">
        <label><h4>Apellido Paterno:</h4></label>
        <InputText
          value={ApellidoP}
          onChange={(e) => setApellidoP(e.target.value)}
          required
          autoFocus
          className={classNames({'p-invalid': OpenForm&&!ApellidoP})} />
        {OpenForm&&!ApellidoP&&<small className="p-invalid">Campo requerido</small>}
      </div>
      {/* Apellido Materno */}
      <div className="p-field">
        <label><h4>Apellido Materno:</h4></label>
        <InputText
          value={ApellidoM}
          onChange={(e) => setApellidoM(e.target.value)}
          required
          className={classNames({'p-invalid': OpenForm&&!ApellidoM})} />
        {OpenForm&&!ApellidoM&&<small className="p-invalid">Campo requerido</small>}
      </div>
      {/* Telefono */}
      <div className="p-field">
        <label><h4>Telefono:</h4></label>
        <InputMask
          mask="(99) 9999-9999?"
          value={Telefono}
          onChange={(e) => setTelefono(e.target.value)} />
      </div>
      {/* correo */}
      <div className="p-field">
        <label><h4>Correo:</h4></label>
        <InputText
          value={Correo}
          onChange={(e) => setCorreo(e.target.value)}
          autoFocus
          className={classNames({'p-invalid': OpenForm&&!Correo})} />
        {OpenForm&&!Correo&&<small className="p-invalid">Campo requerido</small>}
      </div>
      <div className="p-field p-col">
        <label><h4>Direccion:</h4></label>
        <InputTextarea
          value={Direccion}
          onChange={(e) => setDireccion(e.target.value)}
          rows={5}
          cols={30}
          autoResize />
      </div>
      <div className="p-field p-col">
        <label><h4>Notas Medicas:</h4></label>
        <InputTextarea
          value={NotasMedicas}
          onChange={(e) => setNotasMedicas(e.target.value)}
          rows={5}
          cols={30}
          autoResize />
      </div>
      <div className="p-field p-col">
        <label><h4>Notas Especiales:</h4></label>
        <InputTextarea
          value={NotasEspe}
          onChange={(e) => setNotasEspe(e.target.value)}
          rows={5}
          cols={30}
          autoResize />
      </div>
      <div className="p-field p-col">
        <label><h4>Alergias:</h4></label>
        <InputTextarea
          value={Alergias}
          onChange={(e) => setAlergias(e.target.value)}
          rows={5}
          cols={30}
          autoResize />
      </div>

    </React.Fragment>
  )

}

export default Formulario;
