import 'primeicons/primeicons.css';
import 'primereact/resources/themes/nova-light/theme.css';
import 'primereact/resources/primereact.css';
import 'primeflex/primeflex.css';
// import '../../index.css';
// import ReactDOM from 'react-dom';

import React,{Component} from 'react';
import {Dialog} from 'primereact/dialog';
import {Panel} from 'primereact/panel';
import {InputText} from 'primereact/inputtext';
import {CarService} from '../service/CarService';
import {DataView,DataViewLayoutOptions} from 'primereact/dataview';
import {Button} from 'primereact/button';
import {Dropdown} from 'primereact/dropdown';

export class DataViewDemo extends Component {
  constructor() {
    super();
    this.state={
      cars: [
        {
          "Nombre": 'Nombre',
          "Cita": "20/10/2020",
          "Nota": 'Nota',
          "Alergias": "Maroon"
        },
        {
          "Nombre": "Nombre",
          "Cita": "20/10/2020",
          "Nota": 'Nota',
          "Alergias": "White"
        },
        {
          "Nombre": "Nombre",
          "Cita": "20/10/2020",
          "Nota": 'Nota',
          "Alergias": "Black"
        },
        {
          "Nombre": "Nombre",
          "Cita": "20/10/2020",
          "Nota": 'Nota',
          "Alergias": "Red"
        },
        {
          "Nombre": "Nombre",
          "Cita": "20/10/2020",
          "Nota": 'Nota',
          "Alergias": "Brown"
        },
        {
          "Nombre": "Nombre",
          "Cita": "20/10/2020",
          "Nota": 'Nota',
          "Alergias": "Green"
        },
        {
          "Nombre": "Nombre",
          "Cita": "20/10/2020",
          "Nota": 'Nota',
          "Alergias": "Blue"
        },
        {
          "Nombre": "Nombre",
          "Cita": "20/10/2020",
          "Nota": 'Nota',
          "Alergias": "Green"
        },
        {
          "Nombre": "Nombre",
          "Cita": "20/10/2020",
          "Nota": 'Nota',
          "Alergias": "Yellow"
        },
        {
          "Nombre": "Nombre",
          "Cita": "20/10/2020",
          "Nota": 'Nota',
          "Alergias": "Yellow"
        },
        {
          "Nombre": "Nombre",
          "Cita": "20/10/2020",
          "Nota": 'Nota',
          "Alergias": "White"
        },
        {
          "Nombre": "Nombre",
          "Cita": "20/10/2020",
          "Nota": 'Nota',
          "Alergias": "Orange"
        },
        {
          "Nombre": "Nombre",
          "Cita": "20/10/2020",
          "Nota": 'Nota',
          "Alergias": "Orange"
        }
      ],
      layout: 'grid',
      selectedCar: null,
      visible: false,
      sortKey: null,
      sortOrder: null,
      sortField: null,
      globalFilter: null
    };
    this.carservice=new CarService();
    this.itemTemplate=this.itemTemplate.bind(this);
    this.onSortChange=this.onSortChange.bind(this);
  }

  componentDidMount() {
    this.carservice.getCarsLarge().then(data => this.setState({cars: data}));
  }

  onSortChange(event) {
    const value=event.value;

    if(value.indexOf('!')===0) {
      this.setState({
        sortOrder: -1,
        sortField: value.substring(1,value.length),
        sortKey: value
      });
    }
    else {
      this.setState({
        sortOrder: 1,
        sortField: value,
        sortKey: value
      });
    }
  }

  renderListItem(car) {
    return (
      <div className="p-col-12">
        <div className="car-details">
          <div>
            <img src={`showcase/demo/images/car/${car.Foto}.png`} srcSet="https://www.primefaces.org/wp-content/uploads/2020/05/placeholder.png" alt={car.Foto} />
            <div className="p-grid">
              <div className="p-col-12">Nombre <b>{car.Nombre}</b></div>
              <div className="p-col-12">Cita: <b>{car.Cita}</b></div>
              <div className="p-col-12">Nota: <b>{car.Nota}</b></div>
              <div className="p-col-12">Alergia: <b>{car.Alergias}</b></div>
            </div>
          </div>
          <Button icon="pi pi-search" onClick={(e) => this.setState({selectedCar: car,visible: true})}></Button>
        </div>
      </div>
    );
  }

  renderGridItem(car) {
    return (
      <div style={{padding: '.5em'}} className="p-col-12 p-md-3">
        <Panel header={car.Nombre} style={{textAlign: 'center'}}>
          <img src={`showcase/demo/images/car/${car.Foto}.png`} srcSet="https://www.primefaces.org/wp-content/uploads/2020/05/placeholder.png" alt={car.brand} />
          <div className="car-detail">{car.Cita} - {car.Nota}</div>
          <Button icon="pi pi-search" onClick={(e) => this.setState({selectedCar: car,visible: true})}></Button>
        </Panel>
      </div>
    );
  }

  itemTemplate(car,layout) {
    if(!car) {
      return;
    }

    if(layout==='list')
      return this.renderListItem(car);
    else if(layout==='grid')
      return this.renderGridItem(car);
  }

  renderCarDialogContent() {
    if(this.state.selectedCar) {
      return (
        <div className="p-grid" style={{fontSize: '16px',textAlign: 'center',padding: '20px'}}>
          <div className="p-col-12" style={{textAlign: 'center'}}>
            <img src={`showcase/demo/images/car/${this.state.selectedCar.Foto}.png`} srcSet="https://www.primefaces.org/wp-content/uploads/2020/05/placeholder.png" alt={this.state.selectedCar.Foto} />
          </div>

          <div className="p-col-4">Nombre </div>
          <div className="p-col-8">{this.state.selectedCar.Nombre}</div>

          <div className="p-col-4">Year: </div>
          <div className="p-col-8">{this.state.selectedCar.Cita}</div>

          <div className="p-col-4">Nota: </div>
          <div className="p-col-8">{this.state.selectedCar.Nota}</div>

          <div className="p-col-4">Alergias: </div>
          <div className="p-col-8">{this.state.selectedCar.Alergias}</div>
        </div>
      );
    }
    else {
      return null;
    }
  }

  renderHeader() {
    const sortOptions=[
      {label: 'Newest First',value: '!year'},
      {label: 'Oldest First',value: 'year'},
      {label: 'Brand',value: 'brand'}
    ];

    return (
      <div className="p-grid">
        <div className="p-col-6" style={{textAlign: 'left'}}>
          <Dropdown options={sortOptions} value={this.state.sortKey} placeholder="Sort By" onChange={this.onSortChange} style={{width: '12em'}} />
        </div>
        <div className="p-col-6 p-md-6">
          <InputText type="search" onInput={(e) => this.setState({globalFilter: e.target.value})} placeholder="Global Search" size="50" />
          {/* <InputText placeholder="Search by brand" onKeyUp={event => this.dv.filter(event.target.value)} /> */}
        </div>
        <div className="p-col-6" style={{textAlign: 'right'}}>
          <DataViewLayoutOptions layout={this.state.layout} onChange={(e) => this.setState({layout: e.value})} />
        </div>
      </div>
    );
  }

  render() {
    const header=this.renderHeader();

    return (
      <div className="dataview-demo">
        <DataView value={this.state.cars} layout={this.state.layout} header={header}
          itemTemplate={this.itemTemplate} paginatorPosition={'both'} paginator={true} rows={10}
          sortOrder={this.state.sortOrder} sortField={this.state.sortField} filter={this.state.globalFilter} emptyMessage="No records found" />

        <Dialog header="Car Details" visible={this.state.visible} modal={true} onHide={() => this.setState({visible: false})}>
          {this.renderCarDialogContent()}
        </Dialog>
      </div>
    );
  }
}


export default DataViewDemo;
