import React,{useState,useEffect,useRef} from 'react';
import Contenedor,{Alert} from '../componentsPrincipales/Alert.jsx'
import {Toolbar} from 'primereact/toolbar';
import {Dialog} from 'primereact/dialog';
import Formulario from './components/formulario.js'
import Fetch from '../componentsPrincipales/Fetch/FetchN.js'

import {Button} from 'primereact/button';
import {InputText} from 'primereact/inputtext';
import {DataTable} from 'primereact/datatable';
import {Column} from 'primereact/column';

const AdminEmpleados=() => {
  const [Data,setData]=useState([])
  const [OpenForm,setOpenForm]=useState(false)

  // nombre: leonardowwwww
  // apellidoP: gutierrezzzzzz
  // apellidoM: juarez
  // fechaNacimiento: 1992-06-28
  // correo: leogutierrez92@hotmail.com
  // genero: masculino
  // telefono: 5525921151
  // celular: 5611122788
  // fechaIngreso: 2021-07-07
  // departamento: 2

  const [Idcat_clientes,setIdcat_clientes]=useState(false)
  const [Nombre,setNombre]=useState()
  const [ApellidoP,setApellidoP]=useState()
  const [ApellidoM,setApellidoM]=useState()
  const [Telefono,setTelefono]=useState()
  const [Correo,setCorreo]=useState()
  const [Direccion,setDireccion]=useState()
  const [NotasMedicas,setNotasMedicas]=useState()
  const [NotasEspe,setNotasEspe]=useState()
  const [Alergias,setAlergias]=useState()

  const [DeleteForm,setDeleteForm]=useState(false)

  const cols=[{field: 'idcat_empleados',header: 'id'},
  {field: 'nombre',header: 'Nombre'},
  {field: 'apellidoP',header: 'pellido P'},
  {field: 'apellidoM',header: 'pellido M'},
  {field: 'nombreEmpresa',header: 'Empresa'},
  {field: 'nombreDepartamento',header: 'Departamento'}];



  useEffect(() => {
    funget();
  },[]);

  const clearData=() => {
    setIdcat_clientes(false)
    setNombre()
    setApellidoP()
    setApellidoM()

  }

  const validSaveUpdate=() => {
    if(Idcat_clientes) {
      return true;
    } else {
      return false;
    }
  }

  const funget=() => {
    Fetch('empleados/')
      .then(response => {
        setData(response.data)
      }).catch(err => {
        console.log(err);
        Alert('warn',err);
      });
  }

  const saveDataEdit=() => {
    if(validSaveUpdate()) {
      funUpdate();
      funget();
      setOpenForm(false);
      clearData();
    } else {
      funInsert();
      funget();
      setOpenForm(false);
      clearData();
    }
  }


  const funInsert=() => {

    let formData=new FormData();
    if(Nombre!==undefined) formData.append("nombre",Nombre);
    if(ApellidoP!==undefined) formData.append("apellidoP",ApellidoP);
    if(ApellidoM!==undefined) formData.append("apellidoM",ApellidoM);
    if(Telefono!==undefined) formData.append("telefono",Telefono.replace(/[()]/gi,'').replace(/-/gi,'').replace(/[ ]/gi,''));
    if(Correo!==undefined) formData.append("correo",Correo);
    if(Direccion!==undefined) formData.append("direccion",Direccion);
    if(Alergias!==undefined) formData.append("alergias",Alergias);

    Fetch(`api/clientes`,{
      method: "POST",
      body: formData
    }).then(response => {
      if(response.status) {
        Alert('success','Nuevo Usuario Registrado!!!');
        clearData()
      } else {
        Alert('warn',response.err);
      }
    }).catch(err => {
      console.log(err);
      Alert('warn',err);
    });
  };

  const funUpdate=() => {
    let formData=new FormData();
    formData.append("id",Idcat_clientes);
    formData.append("nombre",Nombre);
    formData.append("apellidoP",ApellidoP);
    formData.append("apellidoM",ApellidoM);
    formData.append("telefono",Telefono);
    formData.append("correo",Correo);
    formData.append("direccion",Direccion);
    formData.append("alergias",Alergias);

    Fetch(`api/clientes`,{
      method: "patch",
      body: formData
    }).then(response => {
      if(response.status) {
        Alert('success','Informacion Actualizada!!!');
      }
    }).catch(err => {
      console.log(err);
      Alert('warn',err);
    });
  };

  const funDeleted=() => {
    Fetch(`api/clientes/${Idcat_clientes}`,{
      method: "DELETE"
    }).then(response => {
      if(response.status) {
        Alert('success','Registro Eliminado!!!');
      }
    }).catch(err => {
      console.log(err);
      Alert('warn',err);
    });
  };

  const DialogFooter=(
    <>
      <Button
        label="Cancelar"
        icon="pi pi-times"
        className="p-button-text"
        onClick={() => {
          clearData()
          setOpenForm(false)
        }}
      />
      <Button
        label="Guardar"
        icon="pi pi-check"
        className="p-button-text"
        onClick={() => saveDataEdit()}
      />
    </>
  );

  const deleteDialogFooter=(
    <>
      <Button
        label="No"
        icon="pi pi-times"
        className="p-button-text"
        onClick={() => {setDeleteForm(false)}}
      />
      <Button
        label="Yes"
        icon="pi pi-check"
        className="p-button-text"
        onClick={() => {
          funDeleted()
          funget()
          setDeleteForm(false)
        }}
      />
    </>
  );

  const DataEdit=(rowData) => {
    if(rowData.idcat_clientes)
      setIdcat_clientes(rowData.idcat_clientes)
    setNombre(rowData.nombre)
    setApellidoP(rowData.apellidoP)
    setApellidoM(rowData.apellidoM)
    setTelefono(rowData.telefono)
    setCorreo(rowData.correo)
    setDireccion(rowData.direccion)
    setNotasMedicas(rowData.notas_medicas)
    setNotasEspe(rowData.notas_especiales)
    setAlergias(rowData.alergias)
  }

  const leftContents=(
    <React.Fragment>
      <Button
        label="Nuevo"
        icon="pi pi-plus"
        className="p-button-success p-mr-2"
        onClick={() => {
          clearData()
          setOpenForm(true)
        }}
      />
    </React.Fragment>
  )

  const exportColumns=cols.map(col => ({title: col.header,dataKey: col.field}));
  const [globalFilter,setGlobalFilter]=useState('');
  const dt=useRef(null);

  const exportCSV=(selectionOnly) => {
    dt.current.exportCSV({selectionOnly});
  }

  const exportPdf=() => {
    import('jspdf').then(jsPDF => {
      import('jspdf-autotable').then(() => {
        const doc=new jsPDF.default(0,0);
        doc.autoTable(exportColumns,Data[0]);
        doc.save('Empleados.pdf');
      })
    })
  }

  const exportExcel=() => {
    import('xlsx').then(xlsx => {
      const worksheet=xlsx.utils.json_to_sheet(Data[0]);
      const workbook={Sheets: {'data': worksheet},SheetNames: ['data']};
      const excelBuffer=xlsx.write(workbook,{bookType: 'xlsx',type: 'array'});
      saveAsExcelFile(excelBuffer,'empleados');
    });
  }

  const saveAsExcelFile=(buffer,fileName) => {
    import('file-saver').then(FileSaver => {
      let EXCEL_TYPE='application/vnd.openxmlformats-officedocument.spreadsheetml.sheet;charset=UTF-8';
      let EXCEL_EXTENSION='.xlsx';
      const data=new Blob([buffer],{
        type: EXCEL_TYPE
      });
      FileSaver.saveAs(data,fileName+'_export_'+new Date().getTime()+EXCEL_EXTENSION);
    });
  }


  const reset=() => {
    setGlobalFilter('');
    dt.current.reset();
  }
  const header=(
    <div className="table-header">
      <Button type="button" icon="pi pi-file-o" onClick={() => exportCSV(false)} className="p-mr-2" data-pr-tooltip="CSV" />
      <Button type="button" icon="pi pi-file-excel" onClick={exportExcel} className="p-button-success p-mr-2" data-pr-tooltip="XLS" />
      <Button type="button" icon="pi pi-file-pdf" onClick={exportPdf} className="p-button-warning p-mr-2" data-pr-tooltip="PDF" />
      <Button type="button" label="Clear" className="p-button-outlined" icon="pi pi-filter-slash" onClick={reset} />
      <span className="p-input-icon-left">
        <i className="pi pi-search" />
        <InputText type="search" value={globalFilter} onChange={(e) => setGlobalFilter(e.target.value)} placeholder="Global Search" />
      </span>
    </div>
  );

  return (
    <React.Fragment>
      <Contenedor />
      <div className="datatable-crud-demo">
        <div className="card">
          <Toolbar left={leftContents} />
        </div>
      </div>

      <DataTable header={header} ref={dt} globalFilter={globalFilter} value={Data[0]} rows={10} paginator selectionMode="single"
        scrollable style={{"max-width": '100%',"min-width": '700px'}}
        resizableColumns columnResizeMode="fit"
        onSelectionChange={(e) => {
          console.log(e.value)
        }}
      >
        {
          cols.map((col,index) => <Column key={index} field={col.field} header={col.header} />)
        }

      </DataTable>

      <Dialog
        visible={OpenForm}
        style={{width: '50%'}}
        header="Clientes"
        modal
        className="p-fluid"
        footer={DialogFooter}
        onHide={() => {setOpenForm(false)}}>
        {/* <Panel header="Producto"> */}
        {/* Campo de clave */}
        {/* <div className="p-grid"> */}
        {OpenForm? (<Formulario
          Idcat_clientes={Idcat_clientes} setIdcat_clientes={setIdcat_clientes}
          Nombre={Nombre} setNombre={setNombre}
          ApellidoP={ApellidoP} setApellidoP={setApellidoP}
          ApellidoM={ApellidoM} setApellidoM={setApellidoM}
          Telefono={Telefono} setTelefono={setTelefono}
          Correo={Correo} setCorreo={setCorreo}
          Direccion={Direccion} setDireccion={setDireccion}
          NotasMedicas={NotasMedicas} setNotasMedicas={setNotasMedicas}
          NotasEspe={NotasEspe} setNotasEspe={setNotasEspe}
          Alergias={Alergias} setAlergias={setAlergias}
          OpenForm={OpenForm} setOpenForm={setOpenForm} />):''}
        {/* </div> */}
        {/* </Panel> */}
      </Dialog>

      <Dialog
        visible={DeleteForm}
        style={{width: '50%'}}
        header="Confirm"
        modal
        footer={deleteDialogFooter}
        onHide={() => {setDeleteForm(false)}}>
        <div className="confirmation-content">
          <i className="pi pi-exclamation-triangle p-mr-3" style={{fontSize: '2rem'}} />
          {DataEdit&&<span>Estas seguro de eliminar registro?</span>}
        </div>
      </Dialog>

    </React.Fragment>
  );


}

export default AdminEmpleados;
