import React,{Component} from 'react';
import classNames from 'classnames';
import {DataTable} from 'primereact/datatable';
import {Column} from 'primereact/column';
import {Button} from 'primereact/button';
// import {FileUpload} from 'primereact/fileupload';
import {Toolbar} from 'primereact/toolbar';
// import {InputTextarea} from 'primereact/inputtextarea';
import {InputNumber} from 'primereact/inputnumber';
import {Dialog} from 'primereact/dialog';
import {InputText} from 'primereact/inputtext';
// import {Panel} from 'primereact/panel';
import Contenedor,{Alert} from '../../componentsPrincipales/Alert.jsx'
import Fetch from '../../componentsPrincipales/Fetch/FetchN.js'

export class DataTableCrud extends Component {
  constructor(props) {
    super(props);
    this.state={
      emptyMercancia: {
        idmedicamentos: 0,
        clave: '',
        descripcion: '',
        costoCompra: '',
        costoPublico: '',
        stock: ''
      },
      data: null,
      Mercancias: null,
      MercanciaDialog: false,
      deleteMercanciaDialog: false,
      deleteMercanciasDialog: false,
      Mercancia: this.emptyMercancia,
      selectedMercancias: null,
      submitted: false,
      globalFilter: null
    };
  }

  componentDidMount() {
    this.getMercancias();
  }

  getMercancias() {
    Fetch('Mercancia/getMercancia').then(response => {
      this.setState({data: response.data});
    });
  }

  insertMercancias(data) {

    let formData=new FormData();
    formData.append("idmedicamentos",data.idmedicamentos);
    formData.append("clave",data.clave);
    formData.append("descripcion",data.descripcion);
    formData.append("costoCompra",data.costoCompra);
    formData.append("costoPublico",data.costoPublico);
    formData.append("stock",data.stock);

    Fetch(`Mercancia/insertMercancia`,{
      method: "POST",
      body: formData
    }).then(response => {
      if(response.status) {
        Alert('success','Mercancia Nuevo Registrado!!!');
        this.getMercancias();
      }
    });
  }

  updateMercancias(data) {
    let formData=new FormData();
    formData.append("idmedicamentos",data.idmedicamentos);
    formData.append("clave",data.clave);
    formData.append("descripcion",data.descripcion);
    formData.append("costoCompra",data.costoCompra);
    formData.append("costoPublico",data.costoPublico);
    formData.append("stock",data.stock);

    Fetch(`Mercancia/updateMercancia`,{
      method: "POST",
      body: formData
    }).then(response => {
      if(response.status) {
        Alert('success','Informacion Actualizada!!!');
        this.getMercancias();
      }
    });
  }

  openNew=() => {
    this.setState({
      Mercancia: this.emptyMercancia,
      submitted: false,
      MercanciaDialog: true
    });
  }

  hideDialog=() => {
    this.setState({
      submitted: false,
      MercanciaDialog: false
    });
  }

  hideDeleteMercanciaDialog=() => {
    this.setState({deleteMercanciaDialog: false});
  }

  hideDeleteMercanciasDialog=() => {
    this.setState({deleteMercanciasDialog: false});
  }

  saveMercancia=() => {
    let state={submitted: true};
    console.log(this.state.Mercancia.idmedicamentos);
    if(this.state.Mercancia.clave) {
      let Mercancias=[this.state.Mercancias];
      if(this.state.Mercancia.idmedicamentos) {
        this.updateMercancias(this.state.Mercancia);
      }
      else {
        this.insertMercancias(this.state.Mercancia);
      }
      state={
        ...state,
        Mercancias,
        MercanciaDialog: false,
        Mercancia: this.emptyMercancia
      };
    }
    this.setState(state);
  }

  editMercancia=(Mercancia) => {
    this.setState({
      Mercancia: {...Mercancia},
      MercanciaDialog: true
    });
  }

  confirmDeleteMercancia=(Mercancia) => {
    this.setState({
      Mercancia,
      deleteMercanciaDialog: true
    });
  }

  deleteMercancia=() => {
    let Mercancias=this.state.Mercancias.filter(val => val.id!==this.state.data.idmedicamentos);
    this.setState({
      Mercancias,
      deleteMercanciaDialog: false,
      Mercancia: this.emptyMercancia
    });
    Alert('success','Mercancia Eliminado')
  }

  findIndexById=(id) => {
    let index=-1;
    console.log(id)
    for(let i=0;i<this.state.data.length;i++) {
      if(this.state.data[i].idmedicamentos===id) {
        index=i;
        break;
      }
    }
    return index;
  }

  exportCSV=() => {
    this.dt.exportCSV();
  }

  confirmDeleteSelected=() => {
    this.setState({deleteMercanciasDialog: true});
  }

  deleteSelectedMercancias=() => {
    let Mercancias=this.state.Mercancias.filter(val => !this.state.selectedMercancias.includes(val));
    this.setState({
      Mercancias,
      deleteMercanciasDialog: false,
      selectedMercancias: null
    });
    this.toast.show({severity: 'success',summary: 'Successful',detail: 'Mercancias Deleted',life: 3000});
  }



  onInputChange=(e,name) => {
    const val=(e.target&&e.target.value)||'';
    let Mercancia={...this.state.Mercancia};
    Mercancia[`${name}`]=val;

    this.setState({Mercancia});
  }

  onInputNumberChange=(e,name) => {
    const val=e.value||0;
    let Mercancia={...this.state.Mercancia};
    Mercancia[`${name}`]=val;

    this.setState({Mercancia});
  }


  leftToolbarTemplate=() => {
    return (
      <>
        <Button label="Nuevo" icon="pi pi-plus" className="p-button-success p-mr-2" onClick={this.openNew} />
        <Button label="Borrar" icon="pi pi-trash" className="p-button-danger" onClick={this.confirmDeleteSelected} disabled={!this.state.selectedMercancias||!this.state.selectedMercancias.length} />
      </>
    )
  }

  actionBodyTemplate=(rowData) => {
    return (
      <>
        <Button icon="pi pi-pencil" className="p-button-rounded p-button-success p-mr-2" onClick={() => this.editMercancia(rowData)} />
        <Button icon="pi pi-trash" className="p-button-rounded p-button-warning" onClick={() => this.confirmDeleteMercancia(rowData)} />
      </>
    );
  }

  render() {
    const header=(
      <div className="table-header">
        <h5 className="p-m-0">Manage Mercancias</h5>
        <span className="p-input-icon-left">
          <i className="pi pi-search" />
          <InputText type="search" onInput={(e) => this.setState({globalFilter: e.target.value})} placeholder="Buscar..." />
        </span>
      </div>
    );
    const MercanciaDialogFooter=(
      <>
        <Button label="Cancelar" icon="pi pi-times" className="p-button-text" onClick={this.hideDialog} />
        <Button label="Guardar" icon="pi pi-check" className="p-button-text" onClick={this.saveMercancia} />
      </>
    );
    const deleteMercanciaDialogFooter=(
      <>
        <Button label="No" icon="pi pi-times" className="p-button-text" onClick={this.hideDeleteMercanciaDialog} />
        <Button label="Yes" icon="pi pi-check" className="p-button-text" onClick={this.deleteMercancia} />
      </>
    );
    const deleteMercanciasDialogFooter=(
      <>
        <Button label="No" icon="pi pi-times" className="p-button-text" onClick={this.hideDeleteMercanciasDialog} />
        <Button label="Yes" icon="pi pi-check" className="p-button-text" onClick={this.deleteSelectedMercancias} />
      </>
    );

    return (
      <div className="datatable-crud-demo">
        {/* Contenedor de Alerts */}
        <Contenedor />
        <div className="card">
          <Toolbar className="p-mb-4">
            {this.leftToolbarTemplate()}
            {/* {this.rightToolbarTemplate()} */}
          </Toolbar>
          <DataTable
            ref={(el) => this.dt=el}
            value={this.state.data}
            selection={this.state.selectedMercancias}
            onSelectionChange={(e) => this.setState({selectedMercancias: e.value})}
            dataKey="id"
            paginator
            rows={10}
            rowsPerPageOptions={[2,5,10,25]}
            paginatorTemplate="FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink CurrentPageReport RowsPerPageDropdown"
            currentPageReportTemplate=" {first} de {last} de {totalRecords} Mercancias"
            globalFilter={this.state.globalFilter}
            header={header}>
            {/* <Column selectionMode="multiple" headerStyle={{width: '3rem'}}></Column> */}
            <Column field="idmedicamentos" header="idmedicamentos" sortable></Column>
            <Column field="clave" header="clave" sortable></Column>
            <Column field="descripcion" header="descripcion" sortable></Column>
            <Column field="costoCompra" header="costoCompra" sortable></Column>
            <Column field="costoPublico" header="costoPublico" sortable></Column>
            <Column field="stock" header="stock" sortable></Column>
            <Column body={this.actionBodyTemplate}></Column>
          </DataTable>
        </div>

        <Dialog
          visible={this.state.MercanciaDialog}
          style={{width: '60%'}}
          header="Producto"
          modal
          className="p-fluid"
          footer={MercanciaDialogFooter}
          onHide={this.hideDialog}>
          {/* <Panel header="Producto"> */}
          {/* Campo de clave */}
          <div className="p-grid">
            <div className="p-field p-col">
              <label htmlFor="clave">Clave</label>
              <InputText
                value={this.state.Mercancia.clave}
                onChange={(e) => this.onInputChange(e,'clave')}
                required
                autoFocus
                className={classNames({'p-invalid': this.state.submitted&&!this.state.Mercancia.clave})} />
              {this.state.submitted&&!this.state.Mercancia.clave&&<small className="p-invalid">clave es requerido.</small>}
            </div>
            {/* Campo de Descripcion */}
            <div className="p-field p-col">
              <label htmlFor="descripcion">Descripcion</label>
              <InputText
                value={this.state.Mercancia.descripcion}
                onChange={(e) => this.onInputChange(e,'descripcion')}
                required
                autoFocus
                className={classNames({'p-invalid': this.state.submitted&&!this.state.Mercancia.descripcion})} />
              {this.state.submitted&&!this.state.Mercancia.descripcion&&<small className="p-invalid">Apellido Paterno es required.</small>}
            </div>
            {/* Stock */}
            <div className="p-field p-col">
              <label htmlFor="descripcion">stock</label>
              <InputText
                value={this.state.Mercancia.stock}
                onChange={(e) => this.onInputChange(e,'stock')}
                autoFocus
                className={classNames({'p-invalid': this.state.submitted&&!this.state.Mercancia.stock})} />
            </div>
            {/* Campo de Costo de Compra */}
            <div className="p-field p-col">
              <label htmlFor="costoCompra">Costo de Compra</label>
              <InputText
                value={this.state.Mercancia.costoCompra}
                onChange={(e) => this.onInputChange(e,'costoCompra')}
                required
                autoFocus
                className={classNames({'p-invalid': this.state.submitted&&!this.state.Mercancia.name})} />
              {this.state.submitted&&!this.state.Mercancia.name&&<small className="p-invalid">Name is required.</small>}
            </div>
            {/* Costo a Publico */}
            <div className="p-field p-col">
              <label htmlFor="costoPublico">Costo a Publico</label>
              <InputNumber id="withoutgrouping"
                value={this.state.Mercancia.costoPublico}
                onChange={(e) => this.onInputChange(e,'costoPublico')}
                mode="decimal" useGrouping={false}
                className={classNames({'p-invalid': this.state.submitted&&!this.state.Mercancia.costoPublico})} />
            </div>
          </div>
          {/* </Panel> */}
        </Dialog>

        <Dialog visible={this.state.deleteMercanciaDialog} style={{width: '50%'}} header="Confirm" modal footer={deleteMercanciaDialogFooter} onHide={this.hideDeleteMercanciaDialog}>
          <div className="confirmation-content">
            <i className="pi pi-exclamation-triangle p-mr-3" style={{fontSize: '2rem'}} />
            {this.state.Mercancia&&<span>Estas seguro de eliminar a:  <b>{this.state.Mercancia.clave} {this.state.Mercancia.descripcion} {this.state.Mercancia.costoCompra}</b>?</span>}
          </div>
        </Dialog>

        <Dialog visible={this.state.deleteMercanciasDialog} style={{width: '50%'}} header="Confirm" modal footer={deleteMercanciasDialogFooter} onHide={this.hideDeleteMercanciasDialog}>
          <div className="confirmation-content">
            <i className="pi pi-exclamation-triangle p-mr-3" style={{fontSize: '2rem'}} />
            {this.state.Mercancia&&<span>Estas seguro de eliminar los Mercancias seleccionados ?</span>}
          </div>
        </Dialog>
      </div>
    );
  }
}

export default DataTableCrud;
