import React,{Component} from 'react';
import classNames from 'classnames';
import {DataTable} from 'primereact/datatable';
import {Column} from 'primereact/column';
import {Button} from 'primereact/button';
// import {FileUpload} from 'primereact/fileupload';
import {Toolbar} from 'primereact/toolbar';
import {InputTextarea} from 'primereact/inputtextarea';
import {InputNumber} from 'primereact/inputnumber';
import {Dialog} from 'primereact/dialog';
import {InputText} from 'primereact/inputtext';
import {Panel} from 'primereact/panel';
import Contenedor,{Alert} from '../../componentsPrincipales/Alert.jsx'
import Fetch from '../../componentsPrincipales/Fetch/FetchN.js'

export class DataTableCrud extends Component {

  emptyCliente={
    IdC: 0,
    Nombre: '',
    ApellidoP: '',
    ApellidoM: '',
    Telefono: '',
    Correo: '',
    Direccion: '',
    NotasMedicas: '',
    NotasEspeciales: '',
    Alergias: ''
  };

  constructor(props) {
    super(props);
    this.state={
      data: null,
      Clientes: null,
      ClienteDialog: false,
      deleteClienteDialog: false,
      deleteClientesDialog: false,
      Cliente: this.emptyCliente,
      selectedClientes: null,
      submitted: false,
      globalFilter: null
    };
  }

  componentDidMount() {
    this.getClientes();
  }

  getClientes() {
    Fetch('Clientes/getClientes').then(response => {
      this.setState({data: response.data});
    });
  }

  insertClientes(data) {

    let formData=new FormData();
    formData.append("IdC",data.IdC);
    formData.append("Nombre",data.Nombre);
    formData.append("ApellidoP",data.ApellidoP);
    formData.append("ApellidoM",data.ApellidoM);
    formData.append("Telefono",data.Telefono);
    formData.append("Correo",data.Correo);
    formData.append("Direccion",data.Direccion);
    formData.append("NotasMedicas",data.NotasMedicas);
    formData.append("NotasEspeciales",data.NotasEspeciales);
    formData.append("Alergias",data.Alergias);

    Fetch(`Clientes/insertClientes`,{
      method: "POST",
      body: formData
    }).then(response => {
      if(response.status) {
        Alert('success','Cliente Nuevo Registrado!!!');
        this.getClientes();
      }
    });
  }

  updateClientes(data) {
    let formData=new FormData();
    formData.append("IdC",data.IdC);
    formData.append("Nombre",data.Nombre);
    formData.append("ApellidoP",data.ApellidoP);
    formData.append("ApellidoM",data.ApellidoM);
    formData.append("Telefono",data.Telefono);
    formData.append("Correo",data.Correo);
    formData.append("Direccion",data.Direccion);
    formData.append("NotasMedicas",data.NotasMedicas);
    formData.append("NotasEspeciales",data.NotasEspeciales);
    formData.append("Alergias",data.Alergias);

    Fetch(`Clientes/updateClientes`,{
      method: "POST",
      body: formData
    }).then(response => {
      if(response.status) {
        Alert('success','Informacion Actualizada!!!');
        this.getClientes();
      }
    });
  }

  openNew=() => {
    this.setState({
      Cliente: this.emptyCliente,
      submitted: false,
      ClienteDialog: true
    });
  }

  hideDialog=() => {
    this.setState({
      submitted: false,
      ClienteDialog: false
    });
  }

  hideDeleteClienteDialog=() => {
    this.setState({deleteClienteDialog: false});
  }

  hideDeleteClientesDialog=() => {
    this.setState({deleteClientesDialog: false});
  }

  saveCliente=() => {
    let state={submitted: true};
    console.log(this.state.Cliente.IdC);
    if(this.state.Cliente.Nombre) {
      let Clientes=[this.state.Clientes];
      if(this.state.Cliente.IdC) {
        this.updateClientes(this.state.Cliente);
      }
      else {
        this.insertClientes(this.state.Cliente);
      }
      state={
        ...state,
        Clientes,
        ClienteDialog: false,
        Cliente: this.emptyCliente
      };
    }
    this.setState(state);
  }

  editCliente=(Cliente) => {
    this.setState({
      Cliente: {...Cliente},
      ClienteDialog: true
    });
  }

  confirmDeleteCliente=(Cliente) => {
    this.setState({
      Cliente,
      deleteClienteDialog: true
    });
  }

  deleteCliente=() => {
    let Clientes=this.state.Clientes.filter(val => val.id!==this.state.data.IdC);
    this.setState({
      Clientes,
      deleteClienteDialog: false,
      Cliente: this.emptyCliente
    });
    Alert('success','Cliente Eliminado')

  }

  findIndexById=(id) => {
    let index=-1;
    console.log(id)
    for(let i=0;i<this.state.data.length;i++) {
      if(this.state.data[i].IdC===id) {
        index=i;
        break;
      }
    }
    return index;
  }

  exportCSV=() => {
    this.dt.exportCSV();
  }

  confirmDeleteSelected=() => {
    this.setState({deleteClientesDialog: true});
  }

  deleteSelectedClientes=() => {
    let Clientes=this.state.Clientes.filter(val => !this.state.selectedClientes.includes(val));
    this.setState({
      Clientes,
      deleteClientesDialog: false,
      selectedClientes: null
    });
    this.toast.show({severity: 'success',summary: 'Successful',detail: 'Clientes Deleted',life: 3000});
  }

  onCategoryChange=(e) => {
    let Cliente={...this.state.Cliente};
    Cliente['category']=e.value;
    this.setState({Cliente});
  }

  onInputChange=(e,name) => {
    const val=(e.target&&e.target.value)||'';
    let Cliente={...this.state.Cliente};
    Cliente[`${name}`]=val;

    this.setState({Cliente});
  }

  onInputNumberChange=(e,name) => {
    const val=e.value||0;
    let Cliente={...this.state.Cliente};
    Cliente[`${name}`]=val;

    this.setState({Cliente});
  }


  leftToolbarTemplate=() => {
    return (
      <>
        <Button label="Nuevo" icon="pi pi-plus" className="p-button-success p-mr-2" onClick={this.openNew} />
        <Button label="Borrar" icon="pi pi-trash" className="p-button-danger" onClick={this.confirmDeleteSelected} disabled={!this.state.selectedClientes||!this.state.selectedClientes.length} />
      </>
    )
  }

  actionBodyTemplate=(rowData) => {
    return (
      <>
        <Button icon="pi pi-pencil" className="p-button-rounded p-button-success p-mr-2" onClick={() => this.editCliente(rowData)} />
        <Button icon="pi pi-trash" className="p-button-rounded p-button-warning" onClick={() => this.confirmDeleteCliente(rowData)} />
      </>
    );
  }

  render() {
    const header=(
      <div className="table-header">
        <h5 className="p-m-0">Manage Clientes</h5>
        <span className="p-input-icon-left">
          <i className="pi pi-search" />
          <InputText type="search" onInput={(e) => this.setState({globalFilter: e.target.value})} placeholder="Buscar..." />
        </span>
      </div>
    );
    const ClienteDialogFooter=(
      <>
        <Button label="Cancelar" icon="pi pi-times" className="p-button-text" onClick={this.hideDialog} />
        <Button label="Guardar" icon="pi pi-check" className="p-button-text" onClick={this.saveCliente} />
      </>
    );
    const deleteClienteDialogFooter=(
      <>
        <Button label="No" icon="pi pi-times" className="p-button-text" onClick={this.hideDeleteClienteDialog} />
        <Button label="Yes" icon="pi pi-check" className="p-button-text" onClick={this.deleteCliente} />
      </>
    );
    const deleteClientesDialogFooter=(
      <>
        <Button label="No" icon="pi pi-times" className="p-button-text" onClick={this.hideDeleteClientesDialog} />
        <Button label="Yes" icon="pi pi-check" className="p-button-text" onClick={this.deleteSelectedClientes} />
      </>
    );

    return (
      <div className="datatable-crud-demo">
        {/* Contenedor de Alerts */}
        <Contenedor />
        <div className="card">
          <Toolbar className="p-mb-4">
            {this.leftToolbarTemplate()}
            {/* {this.rightToolbarTemplate()} */}
          </Toolbar>
          <DataTable
            ref={(el) => this.dt=el}
            value={this.state.data}
            selection={this.state.selectedClientes}
            onSelectionChange={(e) => this.setState({selectedClientes: e.value})}
            dataKey="id"
            paginator
            rows={10}
            rowsPerPageOptions={[2,5,10,25]}
            paginatorTemplate="FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink CurrentPageReport RowsPerPageDropdown"
            currentPageReportTemplate=" {first} de {last} de {totalRecords} Clientes"
            globalFilter={this.state.globalFilter}
            header={header}>
            <Column selectionMode="multiple" headerStyle={{width: '3rem'}}></Column>
            <Column field="IdC" header="IdC" sortable></Column>
            <Column field="Nombre" header="Nombre" sortable></Column>
            <Column field="ApellidoP" header="ApellidoP" sortable></Column>
            <Column field="ApellidoM" header="ApellidoM" sortable></Column>
            <Column field="Telefono" header="Telefono" sortable></Column>
            {/* <Column field="NotasMedicas" header="NotasMedicas" sortable></Column> */}
            <Column field="NotasEspeciales" header="NotasEspeciales" sortable></Column>
            <Column body={this.actionBodyTemplate}></Column>
          </DataTable>
        </div>

        <Dialog
          visible={this.state.ClienteDialog}
          style={{width: '60%'}}
          header="Clientes"
          modal
          className="p-fluid"
          footer={ClienteDialogFooter}
          onHide={this.hideDialog}>
          <Panel header="Datos generales">
            {/* Campo de Nombre */}
            <div className="p-grid">
              <div className="p-field p-col">
                <label htmlFor="Nombre">Nombre(s)</label>
                <InputText
                  value={this.state.Cliente.Nombre}
                  onChange={(e) => this.onInputChange(e,'Nombre')}
                  required
                  autoFocus
                  className={classNames({'p-invalid': this.state.submitted&&!this.state.Cliente.Nombre})} />
                {this.state.submitted&&!this.state.Cliente.Nombre&&<small className="p-invalid">Nombre es requerido.</small>}
              </div>
              {/* Campo de Apellido Paterno */}
              <div className="p-field p-col">
                <label htmlFor="ApellidoP">Apellido Paterno</label>
                <InputText
                  value={this.state.Cliente.ApellidoP}
                  onChange={(e) => this.onInputChange(e,'ApellidoP')}
                  required
                  autoFocus
                  className={classNames({'p-invalid': this.state.submitted&&!this.state.Cliente.ApellidoP})} />
                {this.state.submitted&&!this.state.Cliente.ApellidoP&&<small className="p-invalid">Apellido Paterno es required.</small>}
              </div>
              {/* Campo de Apellido Materno */}
              <div className="p-field p-col">
                <label htmlFor="ApellidoM">Apellido Materno</label>
                <InputText
                  value={this.state.Cliente.ApellidoM}
                  onChange={(e) => this.onInputChange(e,'ApellidoM')}
                  required
                  autoFocus
                  className={classNames({'p-invalid': this.state.submitted&&!this.state.Cliente.name})} />
                {this.state.submitted&&!this.state.Cliente.name&&<small className="p-invalid">Name is required.</small>}
              </div>
            </div>
          </Panel>
          <Panel header="Datos de Contacto">
            <div className="p-grid">
              <div className="p-field p-col">
                <label htmlFor="Telefono">Telefono</label>
                <InputNumber id="withoutgrouping"
                  value={this.state.Cliente.Telefono}
                  onChange={(e) => this.onInputChange(e,'Telefono')}
                  mode="decimal" useGrouping={false}
                  className={classNames({'p-invalid': this.state.submitted&&!this.state.Cliente.Telefono})} />
              </div>
              <div className="p-field p-col">
                <label htmlFor="ApellidoP">Correo</label>
                <InputText
                  value={this.state.Cliente.Correo}
                  onChange={(e) => this.onInputChange(e,'Correo')}
                  autoFocus
                  className={classNames({'p-invalid': this.state.submitted&&!this.state.Cliente.Correo})} />
              </div>
            </div>
            <div className="p-field">
              <label htmlFor="Direccion">Direccion</label>
              <InputTextarea
                value={this.state.Cliente.Direccion}
                onChange={(e) => this.onInputChange(e,'Direccion')}
                rows={5} cols={30} autoResize />
            </div>
          </Panel>
          <Panel header="Datos Medicos">
            <div className="p-field">
              <label htmlFor="Notas Medicas">Notas Medicas</label>
              <InputTextarea
                value={this.state.Cliente.NotasMedicas}
                onChange={(e) => this.onInputChange(e,'NotasMedicas')}
                rows={5} cols={30} autoResize />
            </div>
            <div className="p-field">
              <label htmlFor="Notas Especiales">Notas Especiales</label>
              <InputTextarea
                value={this.state.Cliente.NotasEspeciales}
                onChange={(e) => this.onInputChange(e,'NotasEspeciales')}
                rows={5} cols={30} autoResize />
            </div>
            <div className="p-field">
              <label htmlFor="Alergias">Alergias</label>
              <InputTextarea
                value={this.state.Cliente.Alergias}
                onChange={(e) => this.onInputChange(e,'Alergias')}
                rows={5} cols={30} autoResize />
            </div>
          </Panel>
        </Dialog>

        <Dialog visible={this.state.deleteClienteDialog} style={{width: '50%'}} header="Confirm" modal footer={deleteClienteDialogFooter} onHide={this.hideDeleteClienteDialog}>
          <div className="confirmation-content">
            <i className="pi pi-exclamation-triangle p-mr-3" style={{fontSize: '2rem'}} />
            {this.state.Cliente&&<span>Estas seguro de eliminar a:  <b>{this.state.Cliente.Nombre} {this.state.Cliente.ApellidoP} {this.state.Cliente.ApellidoM}</b>?</span>}
          </div>
        </Dialog>

        <Dialog visible={this.state.deleteClientesDialog} style={{width: '50%'}} header="Confirm" modal footer={deleteClientesDialogFooter} onHide={this.hideDeleteClientesDialog}>
          <div className="confirmation-content">
            <i className="pi pi-exclamation-triangle p-mr-3" style={{fontSize: '2rem'}} />
            {this.state.Cliente&&<span>Estas seguro de eliminar los clientes seleccionados ?</span>}
          </div>
        </Dialog>
      </div>
    );
  }
}

export default DataTableCrud;
