import React from 'react';
import classNames from 'classnames';
import {InputText} from 'primereact/inputtext';
import {Dropdown} from 'primereact/dropdown';
import {Password} from 'primereact/password';
import {ColorPicker} from 'primereact/colorpicker';
import {InputSwitch} from 'primereact/inputswitch';
import {InputNumber} from 'primereact/inputnumber'


const Formulario=({StateData,setStateData,OpenForm}) => {

  return (


    <React.Fragment>
      {/* Campo de Descripcion */}
      <div className="p-field">
        <label htmlFor="descripcion">Descripcion</label>
        <InputText
          value={StateData.descripcion}
          onChange={(e) => setStateData({...StateData,descripcion: e.target.value})}
          required
          autoFocus
          className={classNames({'p-invalid': OpenForm&&!StateData.descripcion})} />
        {OpenForm&&!StateData.descripcion&&<small className="p-invalid">Descripci√≥n es obligatorio</small>}
      </div>
      {/*Campo cantidad*/}
      <div className="p-field">
        <label htmlFor="porcentaje">Porcentaje</label>
        <InputNumber
          min="0"
          max="100"
          value={StateData.porcentaje}
          onChange={(e) => setStateData({...StateData,porcentaje: e.value})}
          required
          autoFocus
          className={classNames({'p-invalid': OpenForm&&!StateData.porcentaje})} />
        {OpenForm&&!StateData.porcentaje&&<small className="p-invalid">Porcentaje es obligatorio.</small>}
      </div>
      <div className="p-field">
        <label htmlFor="estatus">Estatus</label>
        <InputSwitch
          checked={StateData.estatus}
          onChange={(e) => setStateData({...StateData,estatus: e.value})}
          />
        {/* {OpenForm&&!StateData.porcentaje&&<small className="p-invalid">Porcentaje es obligatorio.</small>} */}
      </div>
    </React.Fragment>


  )

}

export default Formulario;
