import React,{useState,useEffect} from 'react';
import Contenedor,{Alert} from '../componentsPrincipales/Alert.jsx'
import {Toolbar} from 'primereact/toolbar';
import {Dialog} from 'primereact/dialog';
import Formulario from './components/formulario.js'
import Fetch from '../componentsPrincipales/Fetch/FetchN.js'

import {Button} from 'primereact/button';
import {InputText} from 'primereact/inputtext';
import {DataTable} from 'primereact/datatable';
import {Column} from 'primereact/column';

const AdminEmpleados=() => {
  const [Data,setData]=useState([])
  const [OpenForm,setOpenForm]=useState(false)

  const [Idcat_clientes,setIdcat_clientes]=useState(false)
  const [Nombre,setNombre]=useState()
  const [ApellidoP,setApellidoP]=useState()
  const [ApellidoM,setApellidoM]=useState()
  const [Telefono,setTelefono]=useState()
  const [Correo,setCorreo]=useState()
  const [Direccion,setDireccion]=useState()
  const [NotasMedicas,setNotasMedicas]=useState()
  const [NotasEspe,setNotasEspe]=useState()
  const [Alergias,setAlergias]=useState()

  const [DeleteForm,setDeleteForm]=useState(false)

  const Columnas=[
    {field: "nombre",header: "Nombre"},
    {field: "apellidoP",header: "ApellidoP"},
    {field: "apellidoM",header: "ApellidoM"},
    {field: "telefono",header: "Telefono"},
    {field: "correo",header: "Correo"}
  ]

  useEffect(() => {
    funget();
  },[]);

  const clearData=() => {
    setIdcat_clientes(false)
    setNombre()
    setApellidoP()
    setApellidoM()
    setTelefono()
    setCorreo()
    setDireccion()
    setNotasMedicas()
    setNotasEspe()
    setAlergias()
  }

  const validSaveUpdate=() => {
    if(Idcat_clientes) {
      return true;
    } else {
      return false;
    }
  }

  const funget=() => {
    Fetch('empleados/')
      .then(response => {
        setData(response.data)
      }).catch(err => {
        console.log(err);
        Alert('warn',err);
      });
  }

  const saveDataEdit=() => {
    if(validSaveUpdate()) {
      funUpdate();
      funget();
      setOpenForm(false);
      clearData();
    } else {
      funInsert();
      funget();
      setOpenForm(false);
      clearData();
    }
  }


  const funInsert=() => {

    let formData=new FormData();
    if(Nombre!==undefined) formData.append("nombre",Nombre);
    if(ApellidoP!==undefined) formData.append("apellidoP",ApellidoP);
    if(ApellidoM!==undefined) formData.append("apellidoM",ApellidoM);
    if(Telefono!==undefined) formData.append("telefono",Telefono.replace(/[()]/gi,'').replace(/-/gi,'').replace(/[ ]/gi,''));
    if(Correo!==undefined) formData.append("correo",Correo);
    if(Direccion!==undefined) formData.append("direccion",Direccion);
    if(Alergias!==undefined) formData.append("alergias",Alergias);

    Fetch(`api/clientes`,{
      method: "POST",
      body: formData
    }).then(response => {
      if(response.status) {
        Alert('success','Nuevo Usuario Registrado!!!');
        clearData()
      } else {
        Alert('warn',response.err);
      }
    }).catch(err => {
      console.log(err);
      Alert('warn',err);
    });
  };


  const funUpdate=() => {
    let formData=new FormData();
    formData.append("id",Idcat_clientes);
    formData.append("nombre",Nombre);
    formData.append("apellidoP",ApellidoP);
    formData.append("apellidoM",ApellidoM);
    formData.append("telefono",Telefono);
    formData.append("correo",Correo);
    formData.append("direccion",Direccion);
    formData.append("alergias",Alergias);

    Fetch(`api/clientes`,{
      method: "patch",
      body: formData
    }).then(response => {
      if(response.status) {
        Alert('success','Informacion Actualizada!!!');
      }
    }).catch(err => {
      console.log(err);
      Alert('warn',err);
    });
  };

  const funDeleted=() => {
    Fetch(`api/clientes/${Idcat_clientes}`,{
      method: "DELETE"
    }).then(response => {
      if(response.status) {
        Alert('success','Registro Eliminado!!!');
      }
    }).catch(err => {
      console.log(err);
      Alert('warn',err);
    });
  };

  const DialogFooter=(
    <>
      <Button
        label="Cancelar"
        icon="pi pi-times"
        className="p-button-text"
        onClick={() => {
          clearData()
          setOpenForm(false)
        }
        }
      />
      <Button
        label="Guardar"
        icon="pi pi-check"
        className="p-button-text"
        onClick={() => saveDataEdit()}
      />
    </>
  );

  const deleteDialogFooter=(
    <>
      <Button
        label="No"
        icon="pi pi-times"
        className="p-button-text"
        onClick={() => {setDeleteForm(false)}}
      />
      <Button
        label="Yes"
        icon="pi pi-check"
        className="p-button-text"
        onClick={() => {
          funDeleted()
          funget()
          setDeleteForm(false)
        }}
      />
    </>
  );

  const DataEdit=(rowData) => {
    if(rowData.idcat_clientes)
      setIdcat_clientes(rowData.idcat_clientes)
    setNombre(rowData.nombre)
    setApellidoP(rowData.apellidoP)
    setApellidoM(rowData.apellidoM)
    setTelefono(rowData.telefono)
    setCorreo(rowData.correo)
    setDireccion(rowData.direccion)
    setNotasMedicas(rowData.notas_medicas)
    setNotasEspe(rowData.notas_especiales)
    setAlergias(rowData.alergias)
  }

  const leftContents=(
    <React.Fragment>
      <Button
        label="Nuevo"
        icon="pi pi-plus"
        className="p-button-success p-mr-2"
        onClick={() => {
          clearData()
